import './InfoPost.css'

export default function InfoPost({post, username}) {
   /*console.log(post)*/
  // перевод даты в читабельный вид
  let dateStart = new Date(post.workStart)
  let dateEnd = new Date(post.workEnd)
  let workTime = new Date(post.workTime)
  let AllTimeWork = new Date(workTime - 3*60*60*1000)
  dateStart = dateStart.toLocaleString()
  dateEnd = dateEnd.toLocaleString()
  AllTimeWork = AllTimeWork.toLocaleTimeString()
  dateStart = dateStart.split(',')
  dateEnd = dateEnd.split(',')

  return (
      <tr className='historytable'>
          <td className='historytabledate'> {post.workdate}</td>
          <td className='historytable'>{post.workstart}</td>
          <td className='historytable'>{post.workend}</td>
          <td className='historytable'>{post.worklong}</td>
          <td className='historytable'>{post.healthstart === '0' ? 'Болен' : 'Здоров'}<br/><br/>{post.healthend === '1' ? 'Здоров' :post.healthend === '0' ? 'Болен' : ''}</td>
          <td className='historytable'>{post.commentstart}<br/><br/>{post.commentend}</td>
      </tr>

  )
}
