import './VacationPost.css'

export default function VacationPost({post, username}) {
  // преобразование даты к читабельному виду
  let dateStart = new Date(post.start)
  let dateEnd = new Date(post.end)
  const AllTimeWork = (dateEnd - dateStart) / (60*60*24*1000)
  dateStart = dateStart.toLocaleString()
  dateEnd = dateEnd.toLocaleString()
  dateStart = dateStart.split(',')
  dateEnd = dateEnd.split(',')
  return (
    <div className='InfoPosts'>
      <div className={`InfoPost`}>
        <div className={`infoPostTable`}>
          <div className='infoPostTableBlock Data'>
            {post.workdate}
          </div >
          <div className='infoPostTableBlock'>
            {post.worked}
          </div >
          <div className='infoPostTableBlock Start'>
            {post.workend.replace(/(^|\s)0/g, "$1")}
          </div>
          <div className='infoPostTableBlock End'>
            {post.typework_id === '3' ? 'Отпуск' :post.typework_id === '4' ? 'Больничный' :post.typework_id === '5' ? 'Другая причина':post.typework_id === '2' ? 'Отгул':''}
          </div>
          <div className='infoPostTableBlock Time'>
            {post.commentstart}
          </div>
        </div>
      </div>
      <hr/>
    </div>
  )
}
