import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = ({targetDepartmentId }) => {
    const [dep, setDep] = useState([])
    const [expandedNodes, setExpandedNodes] = useState([]);

    const shouldExpandNode = (node) => {
        return node.department_tree_to_json.id === targetDepartmentId ||
            (node.department_tree_to_json.Subclasses && node.department_tree_to_json.Subclasses.some(sub => shouldExpandNode(sub)));
    };



    useEffect(() => {
// загрузка всех департаментов
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/')
            if(res.data[0].department_tree_to_json.length===0){
                setDep([])
            }else {
                setDep(res.data)
            }
        }
        const initialExpandedNodes = dep.reduce((acc, node) => {
            if (shouldExpandNode(node)) {
                acc.push(node.department_tree_to_json.id);
            }
            return acc;
        }, []);
        setExpandedNodes(initialExpandedNodes);
        getDeps()
    }, [dep, targetDepartmentId])

    const handleNodeClick = (nodeId) => {
        setExpandedNodes((prevExpandedNodes) => {
            if (prevExpandedNodes.includes(nodeId)) {
                return prevExpandedNodes.filter(id => id !== nodeId);
            } else {
                return [...prevExpandedNodes, nodeId];
            }
        });
    };

    const renderTree = (node) => {
        const isExpanded = expandedNodes.includes(node.department_tree_to_json.id);
        return (
            <TreeView
                key={node.department_tree_to_json.id}
                nodeLabel={
                    <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                        <span className="node">{node.department_tree_to_json.Name}</span>
                    </Link>
                }
                defaultCollapsed={!isExpanded}
                onClick={() => handleNodeClick(node.department_tree_to_json.id)}
            >
                {node.department_tree_to_json.Subclasses &&
                node.department_tree_to_json.Subclasses.map(sub => renderTree(sub))}
            </TreeView>
        );
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map(node => renderTree(node))}
                    </div>
                </div>
            </div>
        </div>
    );
}
export default Sidebar;
