import { Link, useParams } from 'react-router-dom'
import { ArrowBack } from '@material-ui/icons'
import { useState } from 'react'

import './SidebarDeps.css'

export default function SidebarDeps({dep, users, setIsShowDeps}) {
  const depsUsers = users.filter((user) => dep.department_id === user.department_id && user.status !== 4)
  const localUser = JSON.parse(localStorage.getItem('user'))
  const [isShow, setIsShow] = useState(false)
  const depsId = useParams().username

  // если выбран департамент
  if(depsId === dep._id){
    dep.backgroundColor = '#e3e3e3'
  }else{
    dep.backgroundColor = ''
  }


  return (
    <ul className='depsName' style={{paddingLeft: `${dep.margin}px`}}>
      <Link to={`/department/${dep._id}`} className='depsUser' style={{fontWeight: `bold`}}>
        <li onClick={e => setIsShowDeps(e)} value={dep.dep_id} style={{backgroundColor: `${dep.backgroundColor}`}}>{dep.departmentName}</li>
      </Link>
      <hr className='depsHr'/>
      <div className='arrowDown' onClick={() => setIsShow(!isShow)}>
        <ArrowBack className={`arrowIcon ${isShow ? ' open' : ''}`} style={{fontSize:'20px'}}/>
      </div>
      { isShow && <ul className='depsStaff'>
        {depsUsers.map((user, id) => <li key={id}>{localUser.isAdmin 
          ? <Link to={`/profile/${user._id}`} className='depsUser'>{user.username}</Link>
          :<div className='depsUser'>{user.username}</div>
        }</li>)}
      </ul>}
    </ul>
  )
}
