import { ExitToApp } from '@material-ui/icons'
import axios from 'axios'
import {useEffect, useState} from 'react'
import SearchBlock from '../SearchBlock/SearchBlock'
import {Link, useParams} from 'react-router-dom'
import logo from './VTB.png'

import './Topbar.css'
import Status from '../Status/Status'
import Modal from "../Templates/Modal/Modal";

export default function Topbar() {
  useEffect((e) => {
    const fetchUser = async () => {
      const res = await axios.get('/api/user/')
      res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      let filterUsers = filterUser(res.data)
      setAllUsers(filterUsers)
    }

    const fetchUserName = async () => {
      const res = await axios.get('/api/user/'+localUser._id)
      setUser(res.data)
    }
    fetchUserName()
    fetchUser()

  }, [])

  function filterUser(users) {
    return users.filter(user => user.status === 1)
        .concat(users.filter(user => user.status === 2))
        .concat(users.filter(user => user.status === 3))
        .concat(users.filter(user => user.status !== 1 && user.status !== 2 && user.status !== 3 && user.status !== 4))
        .concat(users.filter(user => user.status === 4))
  }

  const localUser = JSON.parse(localStorage.getItem('user'))
  const [user, setUser] = useState([])
  const [searchValue, setSearchValue] = useState("")
  const [allUsers, setAllUsers] = useState([])
  const [findUsers, setFindUsers] = useState([])
  const [isBlock, setIsBlock] = useState(false)

  // выход
  const logOut = () => {
    localStorage.clear()
    window.location.reload()
    window.location.href = '/'
  }

  // получение всех пользователей


      //Поиск пользователя
  const handleKeyPress = (e) => {
    if(e.key === 'Enter' && e.target.value!=='' ){
      setFindUsers(allUsers.filter(user => {return user.user_name.toLowerCase().includes(searchValue.toLowerCase())}))
      setIsBlock(true)
    }
  }
     //Поиск пользователя
  const handleClick =(e)=>{
    if(e.target.value!==''){
      setFindUsers(allUsers.filter(user => {return user.user_name.toLowerCase().includes(searchValue.toLowerCase())}))
      setIsBlock(true)
    }}

  console.log('user.main_department=',user)

  return (
    <div>
      <div className='topbarContainer'>
        <div className="topbarLeft">
          <Status/>
        </div>
        <div className="topbarCenter">

{/*          { <div className="searchbar">
            <Search/>
            <input value={searchValue} onChange={(e) => setSearchValue(e.target.value)} onKeyPress={handleKeyPress} placeholder='Найти...' className="searchInput" />
          </div> }*/}
        </div>
        <div className="topbarRight">
          <div className="topbarLinks">
            <div>
              <Link to={`/department/${localUser.main_department}`} className="topbarLink"><img src={logo} alt='VTB' width='150' height='50'/></Link>
            </div>
            {/*<div className='topPage'><Link to={`/department/${user.main_department}`} className="topbarLink"><span className='topPage'>Главная cтраница</span></Link></div>*/}
            <div className='vr'></div>
            {/*<a href="/#"  className="icon"><h1>&#9776;</h1></a>*/}
            <div className='topPage'><Link to={`/profile/${user.id}`} className="topbarLink"><span>{user.user_name}</span></Link></div><div className='vr'></div>
            {localUser.isAdmin
                ?
            <div className='topPage'>
              <Link to='/Reports' className="topbarLink"><span className='link'>Отчеты</span></Link>
              <div className='vr'></div></div> :''}
            <div className='topPage'>
              <Link to='/information' className="topbarLink"><span className='link'>?</span></Link></div>
          </div>
              <div className="search">
            <input onKeyPress={handleKeyPress} value={searchValue} onChange={(e) => setSearchValue(e.target.value)}  type="search" name="" placeholder="поиск" className="input"/>
                 <button onClick={handleClick} value={searchValue} onChange={(e) => setSearchValue(e.target.value)} type="submit"  className="submit"/>
                </div>
          <div style={{paddingLeft:'50px'}}>
          <ExitToApp  className="exit" onClick={logOut}/>
          </div>
        </div>
      </div>
      {isBlock
        ?  <Modal active={handleClick} setActive={setIsBlock}>
            <h1><p align="center">Поиск</p></h1>
            <h4>Найдено: {findUsers.length}</h4>
            <hr/>
            <form className="modalLoginBox" >
              <div>
              {findUsers.map((user) => <SearchBlock user={user}/>)}
              </div>
              <button className="ModalButton" type="submit" onClick={() => setIsBlock(false)}>
                Закрыть
              </button>
            </form>
          </Modal>
        : <></>
      }
    </div>
  )
}
