import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = () => {
    const [dep, setDep] = useState([]);
    const [selectedNodeId, setSelectedNodeId] = useState(null);

    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            if (res.data[0].department_tree_to_json.length === 0) {
                setDep([]);
            } else {
                setDep(res.data);
                // Устанавливаем id узла, который нужно открыть (замените 'your_number' на ваше число)
                const nodeToOpen = findNodeWithId(res.data, 21);
                if (nodeToOpen) {
                    setSelectedNodeId(nodeToOpen.id);
                }
            }
        };
        getDeps();
    }, []);

    const findNodeWithId = (nodes, idToFind) => {
        for (const node of nodes) {
            console.log('node.department_tree_to_json.id=',node.department_tree_to_json.id,idToFind)
            if (node.department_tree_to_json.id === idToFind) {
                return node.department_tree_to_json;
            }
            if (node.department_tree_to_json.Subclasses.length > 0) {
                const foundNode = findNodeWithId(node.department_tree_to_json.Subclasses, idToFind);
                if (foundNode) return foundNode;
            }
        }
        return null;
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                {dep.length >= 1 ? (
                    <div className="tree">
                        <div className="treeWrapper">
                            {dep.map((node) => (
                                <TreeView
                                    key={node.department_tree_to_json.id}
                                    nodeLabel={(
                                        <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                            <span className="node">{node.department_tree_to_json.Name}</span>
                                        </Link>
                                    )}
                                    defaultCollapsed={!selectedNodeId || node.department_tree_to_json.id !== selectedNodeId}
                                >
                                    {node.department_tree_to_json.Subclasses.map((departments) => (
                                        <TreeView
                                            key={departments.id}
                                            nodeLabel={(
                                                <Link to={`/department/${departments.id}`} className='depsUser'>
                                                    <span className="node">{departments.Name}</span>
                                                </Link>
                                            )}
                                            defaultCollapsed={true}
                                        >
                                            {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                <TreeView
                                                    key={departments1.id}
                                                    nodeLabel={(
                                                        <Link to={`/department/${departments1.id}`} className='depsUser'>
                                                            <span className="node">{departments1.Name}</span>
                                                        </Link>
                                                    )}
                                                    defaultCollapsed={true}
                                                >
                                                    {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                        <Link to={`/department/${departments2.id}`} className='depsUser' key={departments2.id}>
                                                            <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            ))}
                        </div>
                    </div>
                ) : ''}
            </div>
        </div>
    );
};

export default Sidebar;
