import './VacationPost.css'

export default function VacationPost({post, username}) {
  // преобразование даты к читабельному виду
  let dateStart = new Date(post.start)
  let dateEnd = new Date(post.end)
  const AllTimeWork = (dateEnd - dateStart) / (60*60*24*1000)
  dateStart = dateStart.toLocaleString()
  dateEnd = dateEnd.toLocaleString()
  dateStart = dateStart.split(',')
  dateEnd = dateEnd.split(',')
  return (
      <tr style={{border:"1px solid grey"}}>
        <td style={{border:"1px solid grey"}}>{post.workdate}</td>
        <td style={{border:"1px solid grey"}}>{post.worked}</td>
        <td style={{border:"1px solid grey"}}>{post.work.replace(/(^|\s)0/g, "$1")}</td>
        <td style={{border:"1px solid grey"}}>{post.typework_id === '5' ? 'Отпуск' :post.typework_id === '3' ? 'Больничный' :post.typework_id === '7' ? 'Другая причина':post.typework_id === '6' ? 'Отгул':''}</td>
        <td style={{border:"1px solid grey" , minWidth:"350px"}}>{post.commentstart}</td>

      </tr>
  )
}
