import React, {useEffect, useRef, useState} from 'react'
import Pagination from "../Pagination/Pagination";
import ModalVacancies from "../Templates/ModalVacancies/ModalVacancies"
import axios from "axios";
import './VacanciesView.css'
import VacanciesItem from "../VacanciesItem/VacanciesItem";
import Topbar from "../Topbar/Topbar";
import Sidebar from "../Sidebar/Sidebar";
import VacanciesAdd from "../VacanciesAdd/VacanciesAdd";



function VacanciesView(departId) {

    const modalDep = useRef()
    const [filterSelect, setFilterSelect] = useState([])
    const [pressedCompany, setPressedCompany] = useState(false);

    const [isOpen, setIsOpen] = useState(true)


    const [vacancies, setVacancies] = useState([])
    const [isAddingVacancies, setIsAddingVacancies] = useState(false)
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [filteredData, setFilteredData] = useState([]);
    const [vacanciesName, setVacanciesName] = useState([]);
    const [status, setStatus] = useState([])
    const [company, setCompany] = useState([])
    const localUser = JSON.parse(localStorage.getItem('user'))

    useEffect(() => {




        const VacanciesView = async () => {

            const statuses = await axios.get('/api/Vacations/?status=status')
            const companies = await axios.get('/api/Vacations/?company=company')

            setCompany(companies.data)
            setStatus(statuses.data)

            const vacancies_function={
                departId: localUser.departmentId,
                user_id: localUser._id
            }

            const getVacancies = await axios.post('/api/Vacations_function/',vacancies_function)

            if (isOpen) {
                // Фильтрация вакансий по departId
                setVacancies(getVacancies.data[0].get_vacancies); // Установка нового массива вакансий

            }
            console.log('vac=',vacancies)
        }

        VacanciesView()
    }, [isOpen, departId])


    const reset = () => {
        setStartDate('')
        setEndDate('')

    }

    const handleStartDateChange = (e) => {
        setStartDate(e.target.value)
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value)
    }


    const handleCombinedFilter = () => {
        function filterVacanciesByDate(startDate, endDate) {
            return vacancies.map(department => ({
                ...department,
                vacancies: department.vacancies.filter(vacancy => {
                    const vacancyDate = new Date(vacancy.create_date);
                    const start = startDate ? new Date(startDate) : null;
                    const end = endDate ? new Date(endDate) : null;
                    return (!start || vacancyDate >= start) && (!end || vacancyDate <= end);
                })
            }));
        }


        const filteredData = filterVacanciesByDate(startDate, endDate);
        setIsOpen(false)
        setFilteredData(filteredData);
        setFilterSelect([])
        setVacanciesName([])
    }

    const clickAdd = () => {
        setIsAddingVacancies(true);
    }

    const clickBackButton = () => {
        setIsAddingVacancies(false);
    }


    const handleClickFilter = (id,name) => {

        console.log('id&Name=',id,name)


        setPressedCompany(prevState => ({
            ...prevState,
            [id]: !prevState[id],

        }));


    const filterVacanciesByCompanyName = (vacancies, companyName) => {
        return vacancies.map(department => ({
            ...department,
            vacancies: department.vacancies.filter(vacancy => vacancy.company_name === companyName)
        })).filter(department => department.vacancies.length > 0);
    }

        const filteredVacancies = filterVacanciesByCompanyName(vacancies, id);
        console.log(filteredVacancies);
        setVacanciesName(filteredVacancies)
        setIsOpen(false)

    };




    const getColorByStatusId = (statusId) => {

        switch (Number(statusId)) {
            case 1:
                return 'Reserve'; //  Резерв Установите Белый цвет для статуса 1
            case 2:
                return 'Selection'; // Подбор Установите желтый цвет для статуса 2
            case 3:
                return 'Securely'; // Обеспеченно Установите зелёный цвет для статуса 3
            case 4:
                return 'Liquidated'; // Ликвидированно Установите белый прозрачный цвет для статуса 4
            case 5:
                return 'Finalist'; //  Финалист Установите красный цвет для статуса 5
        }
    }
    const [selectedDepartment, setSelectedDepartment] = useState('');
    const [selectedStatus, setSelectedStatus] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState([]);

    const handleSelectChange = (e) => {
        setSelectedDepartment(e.target.value);
    };

    const handleStatusClick = (statusName) => {
        setSelectedStatus((prev) =>
            prev.includes(statusName)
                ? prev.filter((name) => name !== statusName)
                : [...prev, statusName]
        );
    };

    const handleCompanyClick = (companyId) => {
        setSelectedCompany((prev) =>
            prev.includes(companyId)
                ? prev.filter((id) => id !== companyId)
                : [...prev, companyId]
        );
    };

    const filteredVacancies = vacancies.filter((vacancy) => {
        const departmentMatch = selectedDepartment ? vacancy.department_id === parseInt(selectedDepartment) : true;
        const statusMatch = selectedStatus.length > 0 ? selectedStatus.includes(vacancy.status_id) : true;
        const companyMatch = selectedCompany.length > 0 ? selectedCompany.includes(vacancy.company_id) : true;
        return departmentMatch && statusMatch && companyMatch;
    });


    return (
        <>
            <Topbar/>
            <div className="mainContainer">
                <Sidebar/>
                <div className='Vacations'>
                    {isAddingVacancies ? (
                        <button className='btnStaff' onClick={clickBackButton}>Назад</button>
                    ) : (
                        <div>
                            <button className='btnStaff' onClick={() => window.location.href = `/department/${localUser.departmentId}`}>Назад</button>
                            <button className='btnStaff' onClick={clickAdd}>Добавить вакансии</button>
                        </div>
                    )}
                        {isAddingVacancies ? (
                            <VacanciesAdd />
                        ) : (
                            <>
                            <div className="VacationsContainerTop">
                                <h2>Вакансии</h2>
                                <div>
                                    <input
                                        type='date'
                                        placeholder="Выберите дату"
                                        className="ModalInput"
                                        style={{'marginRight': '10px'}}
                                        value={startDate}
                                        onChange={handleStartDateChange}
                                    />—   <input
                                    type='date'
                                    placeholder="Выберите дату"
                                    className="ModalInput"
                                    style={{'marginRight': '10px'}}
                                    value={endDate}
                                    onChange={handleEndDateChange}
                                />
                                    <button className="filterButton" onClick={handleCombinedFilter}>Поиск</button>
                                    <button className="filterButton" onClick={reset}>Сбросить</button>
                                </div>
                                <br/>
                                <div>
                                    <select className="ModalInputUpdateSelect" onChange={handleSelectChange}>
                                        <option value="">Все департаменты</option>
                                        {vacancies.map((dep, index) => (
                                            <option key={index} value={dep.department_id}>
                                                {dep.department}
                                            </option>
                                        ))}
                                    </select>

                                    <div>
                                        {company.map((company, id) => (
                                            <button
                                                key={id}
                                                className='filtersButton'
                                                onClick={() => handleCompanyClick(company.company_id)}
                                                style={{
                                                    boxShadow: selectedCompany.includes(company.company_id) ? "0 0 1px rgba(0,0,0,.5) inset, 0 2px 3px rgba(0,0,0,.5) inset, 0 1px 1px rgba(255,255,255,.1)" : "",
                                                    background: selectedCompany.includes(company.company_id) ? 'gray' : '#1775ee'
                                                }}
                                            >
                                                {company.company_name}
                                            </button>
                                        ))}
                                    </div>

                                    <br/>

                                    <div>
                                        {status.map((status, id) => (
                                            <button
                                                key={id}
                                                className={'filtersButton' + getColorByStatusId(status.id)}
                                                onClick={() => handleStatusClick(status.status_name)}
                                                style={{
                                                    boxShadow: selectedStatus.includes(status.status_name) ? "0 0 1px rgba(0,0,0,.5) inset, 0 2px 3px rgba(0,0,0,.5) inset, 0 1px 1px rgba(255,255,255,.1)" : "",
                                                    background: selectedStatus.includes(status.status_name) ? 'gray' : '',
                                                    opacity: selectedStatus.includes(status.status_name) ? '1' : ''
                                                }}
                                            >
                                                {status.status_name}
                                            </button>
                                        ))}
                                    </div>

                                    <div>
                                        {filteredVacancies.map((vacancy, id) => (
                                            <div className='VacationsContainer' key={id}>
                                                <VacanciesItem vac={vacancy} key={vacancy.id} status={vacancy} department={vacancy.department} vacancies={vacancy.vacancies} />
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>













{/*                                {
                                    filteredData.map(vac => (
                                        <div className='VacationsContainer' key={vac.id}>
                                            <VacanciesItem vac={vac} key={vac.department} status={status} department={vac.department} vacancies={vac.vacancies}/>
                                        </div>
                                    ))

                                }*/}


{/*                                {
                                    !isOpen ?
                                        filterSelect.map((vac, id) => (
                                    <div className='VacationsContainer' key={id}>
                                    <VacanciesItem vac={vac} key={vac.department} status={status} department={vac.department} vacancies={vac.vacancies}/>
                                    </div>
                                    )) : ''

                                }*/}

{/*                                {
                                    vacanciesName.map(vac => (
                                    <div className='VacationsContainer' key={vac.id}>
                                        <VacanciesItem vac={vac} key={vac.department} status={status} department={vac.department} vacancies={vac.vacancies}/>
                                    </div>
                                ))

                                }*/}


                            </>
                        )}
                    </div>
                </div>
        </>
    )
}

export default VacanciesView
