}
        get_users_by_depIsCheckedTrue()
        get_statistics_all_users()
        statusChild()

    }, [departId])


    //Сортировка пользователей по статусам
    function filterUsersFoo(users) {
        return users.filter(user => user.status === 1)
            .concat(users.filter(user => user.status === 2))
            .concat(users.filter(user => user.status === 3))
            .concat(users.filter(user => user.status !== 1 && user.status !== 2 && user.status !== 3 && user.status !== 4))
            .concat(users.filter(user => user.status === 4))
    }


    // Добавление дочерних по checkbox
    const clickcheckbox = async () => {
        setChecked(!checked);

        const info = {
            userId: localUser._id,
            ischeked: !checked
        };
        await axios.post('/api/statusChild/', info);

        const inf = {
            depsId: depsId
        };
        const res = await axios.post('/api/get_users_by_dep/', inf);
        const dep_users = res.data[0]['get_users_by_dep'];

        if (dep_users === null) {
            setUs([]);
        } else {
            dep_users.sort((a, b) => a.user_name.localeCompare(b.user_name));
            let filterUsers = filterUsersFoo(dep_users);
            setUs(filterUsers);
        }
        if(checked===true) {
            setIsJob(!isJob)
        }
        if(!checked===false){
            setIsJob(isJob)
        }
    }


    // Обновляем main department по кнопке изменить в модальном окне
    const handleModalClick = async () => {

        const add = {
            id: localUser._id,
            depsId: depsId
        }
        const res = await axios.put('/api/user', add)
        setModalMain(false)
    }

    // Функция get_users_by_dep по кнопке показать дочернии
    const get_users_by_depIsOpen = async () => {
    if(checked===true){
        setUs([])
    }else{

    }
        if (isJob) {
            const info = {
                depsId: depsId
            }
            const res = await axios.post('/api/get_users_by_dep/', info)
            const dep_users = res.data[0]['get_users_by_dep']

            if (dep_users === null) {

            } else {
                dep_users.sort((a, b) => a.user_name.localeCompare(b.user_name))
                let filterUsers = filterUsersFoo(dep_users)
                setUsersDeps(filterUsers)
                setIsOpen(true)
                setIsJob(!isJob)
            }
        }
    }
    const handleWorkVacation = () => {

        setIsOpenVacation(!isOpenVacation)

    }
    const handleShowVacancies = () => {
        setIsOpen(true)
        setHideButtons(true); // Скрыть кнопки при нажатии "Показать вакансии"
        // Здесь можете добавить другую логику, связанную с показом вакансий
    };

    const handleReturn = () => {
        setHideButtons(false); // Показать кнопки при нажатии "Вернуться"
        // Здесь можете добавить другую логику, связанную с возвратом
    };
    return (
        <div className='staff'>
            {departId && (
                <>
                    {!hideButtons && (
                        <>
                            {checked === true || !isJob ? (
                                <button disabled={checked} onClick={() => window.location.href = `/department/${departId}`} className="btnStaff">
                                    Скрыть дочерние
                                </button>
                            ) : (
                                <button onClick={get_users_by_depIsOpen} className="btnStaff" style={{ width: '155px' }}>
                                    Показать дочерние
                                </button>
                            )}
                            <button onClick={() => setModalMain(true)} className='btnStaff' style={{ float: 'right' }}>Сделать главной</button>
                        </>
                    )}
                    <button className="filterButton" onClick={hideButtons ? handleReturn : handleShowVacancies} style={{ width: '155px' }}>
                        {hideButtons ? 'Вернуться' : 'Показать вакансии'}
                    </button>
                </>
            )}

            <div className="employeeWrapper">
                {isFetching && checked===false
                    ? <div className='isFetching'>Загрузка...</div>
                    : users.length === 0 && getUser.length !== 0
                        ? ''
                        : !!departId  && !checked
                            ? !isOpen
                                ? users.filter(user => user.department_id === departId).map((user, id) => <Employee key={id} user={user}/>)
                                : ''
                            : ''
                }

                {isOpenVacation ? <div>sdfsdf</div>:''}

                {!departId
                    ? statisticsWork.map((user, id) => <Employee key={id} user={user} />)
                    :''

                }

                {
                    checked
                        ? us.map((user, id) => <Employee key={id} user={user}/>)
                        : ''
                }

                {
                    !!isOpen
                        ?
                        UsersDep.map((user, id) => <Employee key={id} user={user}/>)
                        : ""
                }

                {
                    !!getUser
                        ?
                        getUser.map((user, id) => <Employee key={id} user={user}/>)
                        : ""
                }

            </div>


            <Modal active={modalMain} setActive={setModalMain}>
                <h1>Изменение информации:</h1>
                <hr/>
                <form className="modalLoginBox">
                    <label>
                        {<input
                            type='checkbox'
                            onChange={clickcheckbox}
                            checked={checked}
                        />
                        }Добавить дочерние</label>
                    <button className="ModalButton" type="submit" disabled={isFetching} onClick={handleModalClick}>
                        Сделать главным департаментом
                    </button>
                </form>
            </Modal>
        </div>

    )
}
