import "./VcanciesItem.css"
import moment from "moment";


const VacanciesItem = ({ vac, departId }) => {
    const date = moment().format('YYYY-MM-DD');

    // Проверяем, что текущая вакансия относится к нужному отделу
    const isVacancyForDepartment = vac.team_id === departId;

    // Если вакансия относится к нужному отделу, отображаем её
    if (isVacancyForDepartment) {
        return (
            <div className="angry-grid1">
                <div id="item-01">{vac.name}</div>
                <div id="item-11">{date === vac.create_date ? 'NEW' : ''}</div>
                <div id="item-21">{vac.description}</div>
                <div id="item-31"></div>
                <div id="item-41">{moment(vac.create_date).format('DD.MM.YYYY')}</div>
            </div>
        );
    } else {
        // Если вакансия не относится к нужному отделу, возвращаем null
        return null;
    }
};

export default VacanciesItem
