import './InfoReposts.css'
import {useState} from "react";

export default function InfoReposts({posts, username}) {
    console.log("SDFSDF=",posts.workdate)
    // перевод даты в читабельный вид
    let dateStart = new Date(posts.workStart)
    let dateEnd = new Date(posts.workEnd)
    let workTime = new Date(posts.workTime)
    let AllTimeWork = new Date(workTime - 3*60*60*1000)
    dateStart = dateStart.toLocaleString()
    dateEnd = dateEnd.toLocaleString()
    AllTimeWork = AllTimeWork.toLocaleTimeString()
    dateStart = dateStart.split(',')
    dateEnd = dateEnd.split(',')

    return (
        <tr className='info'>
            <td className='infopostdate'> {posts.workdate}</td>
            <td className='infotable'>{posts.workstart}</td>
            <td className='infotable'>{posts.workend}</td>
            <td className='infotable'>{posts.worklong}</td>
        </tr>



    )
}
