          </div >
          <div className='infoPostTableBlock Start'>
            {dateStart[1]}
          </div>
          <div className='infoPostTableBlock End'>
            {dateEnd[0] === 'Invalid Date' ? '' : dateEnd[1]}
          </div>
          <div className='infoPostTableBlock Time'>
            {workTime > 0 ? AllTimeWork : ''}
          </div>
          <div className='infoPostTableBlock Health'>
            {post.healthStart === 0 ? 'Болен' : 'Здоров'}
            <br/>
            {post.healthEnd === 0 ? 'Болен' : post.healthEnd === 1 ? 'Здоров' : ''}
          </div>
          <div className='infoPostTableBlock Health'>
            {post.commentStart}
            <br/>
            {post.commentEnd}
          </div>
        </div>
      </div>
      <hr/>
    </div>
  )
}
