import React from 'react'
import {Link, useParams} from 'react-router-dom'
import axios from 'axios'
import { useEffect, useState } from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = ({ departmentIdToOpen }) => {
    const [dep, setDep] = useState([]);

    // загрузка всех департаментов
    useEffect(() => {
        const getDeps = async () => {
            try {
                const res = await axios.get('/api/department_tree_to_json/');
                setDep(res.data);
            } catch (error) {
                console.error('Error fetching departments:', error);
            }
        };

        getDeps();
    }, []);

    const handleNodeClick = (departmentId) => {
        localStorage.setItem('selectedDepartment', departmentId);
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map((node, i) => {
                            const label = (
                                <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                    <span className="node">{node.department_tree_to_json.Name}</span>
                                </Link>
                            );

                            return (
                                <TreeView
                                    key={node.department_tree_to_json.Name + '|' + i}
                                    nodeLabel={label}
                                    defaultCollapsed={false}
                                    onClick={() => handleNodeClick(node.department_tree_to_json.id)}
                                >
                                    {node.department_tree_to_json.Subclasses.map((departments) => (
                                        <TreeView
                                            key={departments.id}
                                            nodeLabel={(
                                                <Link
                                                    to={`/department/${departments.id}`}
                                                    className='depsUser'
                                                    onClick={() => handleNodeClick(departments.id)}
                                                >
                                                    <span className="node">{departments.Name}</span>
                                                </Link>
                                            )}
                                            defaultCollapsed={true}
                                            collapsed={departmentIdToOpen !== departments.id}
                                        >
                                            {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                <TreeView
                                                    key={departments1.id}
                                                    nodeLabel={(
                                                        <Link
                                                            to={`/department/${departments1.id}`}
                                                            className='depsUser'
                                                            onClick={() => handleNodeClick(departments1.id)}
                                                        >
                                                            <span className="node">{departments1.Name}</span>
                                                        </Link>
                                                    )}
                                                    defaultCollapsed={true}
                                                    collapsed={departmentIdToOpen !== departments1.id}
                                                >
                                                    {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                        <Link
                                                            key={departments2.id}
                                                            to={`/department/${departments2.id}`}
                                                            className='depsUser'
                                                            onClick={() => handleNodeClick(departments2.id)}
                                                        >
                                                            <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            );
                        })}
                    </div>
                </div>
            </div>
            <div></div>
        </div>
    );
};

export default Sidebar
