import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css';
import './Sidebar.css';

const Sidebar = () => {
    const [dep, setDep] = useState([]);
    const [expandedNodes, setExpandedNodes] = useState([]);

    // Загрузка всех департаментов
    useEffect(() => {
        const getDeps = async () => {
            try {
                const res = await axios.get('/api/department_tree_to_json/');
                setDep(res.data);
            } catch (error) {
                console.error('Error fetching departments:', error);
            }
        };

        getDeps();
    }, []);

    const handleNodeClick = (nodeId) => {
        if (expandedNodes.includes(nodeId)) {
            // Если узел уже раскрыт, закрываем его
            setExpandedNodes(expandedNodes.filter((id) => id !== nodeId));
        } else {
            // Если узел не раскрыт, добавляем его в список раскрытых узлов
            setExpandedNodes([...expandedNodes, nodeId]);
        }
    };

    const isNodeExpanded = (nodeId) => {
        return expandedNodes.includes(nodeId);
    };

    const renderTree = (node) => {
        if (!node || !node.department_tree_to_json || !node.department_tree_to_json.id) {
            return null;
        }

        const nodeId = node.department_tree_to_json.id;

        const label = (
            <Link
                to={`/department/${nodeId}`}
                className="level1"
                onClick={() => handleNodeClick(nodeId)}
            >
                <span className="node">{node.department_tree_to_json.Name}</span>
            </Link>
        );

        const isExpanded = isNodeExpanded(nodeId);

        return (
            <TreeView key={nodeId} nodeLabel={label} defaultCollapsed={!isExpanded}>
                {node.department_tree_to_json.Subclasses.map((subNode) => {
                    return renderTree(subNode);
                })}
            </TreeView>
        );
    };

    return (
        <div className="sidebar">
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">{dep.map((node) => renderTree(node))}</div>
                </div>
            </div>
            <div></div>
        </div>
    );
};

export default Sidebar;
