import { ExitToApp } from '@material-ui/icons'
import axios from 'axios'
import { useEffect, useState } from 'react'
import SearchBlock from '../SearchBlock/SearchBlock'
import { Link } from 'react-router-dom'

import './Topbar.css'
import Status from '../Status/Status'

export default function
    Topbar() {

  const user = JSON.parse(localStorage.getItem('user'))

  const [searchValue, setSearchValue] = useState("")
  const [allUsers, setAllUsers] = useState([])
  const [findUsers, setFindUsers] = useState([])
  const [isBlock, setIsBlock] = useState(false)

  // выход
  const logOut = () => {
    localStorage.clear()
    window.location.reload()
    window.location.href = '/'
  }

  // получение всех пользователей. отключено
  useEffect((e) => {
    const fetchUser = async () => {
      const res = await axios.get('/api/user/')
      setAllUsers(res.data)
    }

    fetchUser()
  }, [])

  // поиск пользователя. отключено

  const handleKeyPress = (e) => {
    if(e.key === 'Enter'){
      setFindUsers(allUsers.filter(user => user.user_name.toLowerCase().includes(searchValue.toLowerCase())))
      setIsBlock(true)
    }
  }
const handleClick =()=>{
  setFindUsers(allUsers.filter(user => user.user_name.toLowerCase().includes(searchValue.toLowerCase())))
  setIsBlock(true)
}

  return (
    <div>
      <div className='topbarContainer'>
        <div className="topbarLeft">
          <Status/>
        </div>
        <div className="topbarCenter">
{/*          { <div className="searchbar">
            <Search/>
            <input value={searchValue} onChange={(e) => setSearchValue(e.target.value)} onKeyPress={handleKeyPress} placeholder='Найти...' className="searchInput" />
          </div> }*/}
        </div>
        <div className="topbarRight">
          <div className="topbarLinks">
            <div>
              <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/7/7c/VTB_Logo_2018.svg/2560px-VTB_Logo_2018.svg.png" alt='VTB' width='150' height='50'/>
            </div>
            <div className='one'><Link to={`/department/${user.departmentId}`} className="topbarLink"><span className='link'>Главная cтраница</span></Link></div><div className='vr'></div>
            <div className='one'><Link to={`/profile/${user._id}`} className="topbarLink"><span className='link'>{user.username}</span></Link></div><div className='vr'></div>
            <div className='one'><Link to='/information' className="topbarLink"><span className='link'>Информация</span></Link></div>
            </div>
          <div className="search">
            <input onKeyPress={handleKeyPress} value={searchValue} onChange={(e) => setSearchValue(e.target.value)}  type="search" name="" placeholder="поиск" className="input"/>
            <input onClick={handleClick} onChange={(e) => setSearchValue(e.target.value)} type="submit" value="" className="submit"/>
          </div>
          <ExitToApp  style={{cursor:'pointer',marginLeft:'2%', marginRight:'20px'}} onClick={logOut}/>
        </div>
      </div>
      {isBlock
        ? <div className="searchContainer">
            <div className='searchTop'>
              <h3 className='serachTitle'>Найдено: {findUsers.length}</h3>
              <p>
                <div>
                  <button className='searchBtn' onClick={() => setIsBlock(false)}>Закрыть</button>
                </div>
                <div>
                  sdfsfd
                </div>

              </p></div>
            {findUsers.map((user) => <SearchBlock user={user}/>)}
          </div>
        : <></>
      }
    </div>

  )

}
