import React, {useEffect, useRef, useState} from 'react'
import Topbar from '../Topbar/Topbar'
import {Link} from "react-router-dom";
import './VacanciesView.css'
import axios from "axios";

import Pagination from "../Pagination/Pagination";

function VacanciesView() {
    const [deps, setDeps] = useState([])
    const [users, setUsers] = useState([])
    const [currentPage, setCurrentPage] = useState(1)
    const [postsPerPage] = useState(5)

    const lastPostIndex = currentPage * postsPerPage
    const firstPostIndex = lastPostIndex - postsPerPage
    const currentlyPosts = users.slice(firstPostIndex, lastPostIndex)
    const paginate = pageNumber => setCurrentPage(pageNumber)


    const department = useRef()
    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/departments/')
            setDeps(res.data)
        }

        getDeps()
    }, [])

    const button = async () => {
        const res = await axios.get('/api/user/')
        setUsers(res.data)
        console.log('res=',res.data)
    }


    return (
        <>
            <Topbar/>
            <div className='Vacations'>
                <div className="VacationsContainer">
                    <h2>Вакансии</h2>
                    <br/>
                    <div className="VacationsMain">
                        <div>
                    <input
                        type='search'
                        placeholder="Наименование вакансии"
                        className="ModalInput"
                        style={{'marginRight': '10px'}}
                    />
                    </div>
                        <div>
                        <select ref={department} className='deps'>
                            <option value=''>Выберите подразделение</option>
                            {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
                        </select>
                        </div>
                            <div>
                        <button className="filterButton" onClick={button}>Найти</button>
                        <button className="filterButton" >Сбросить</button>
                            </div>
                        </div>
                    <br/>

                   <div>
                    <input
                        type='date'
                        placeholder="Выберите дату"
                        className="ModalInput"

                        style={{'marginRight': '10px'}}
                    />
                        —  <input
                            type='date'
                            placeholder="Выберите дату"
                            className="ModalInput"
                            style={{'marginRight': '10px'}}
                        />
                    </div>

                </div>

                    <div>
                        {currentlyPosts.map((user, id) => (
                            <div className='VacationsContainer' key={id}><label>
                                {user.user_name}</label><p>{user.email}</p></div>
                        ))}
                        <Pagination postsPerPage={postsPerPage} totalPosts={users.length} paginate={paginate}
                                    currentPage={currentPage}/>
                </div>

            </div>

        </>
    )
}
export default VacanciesView
