import {ExitToApp} from '@material-ui/icons'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {Link, useParams} from 'react-router-dom'
import SearchBlock from '../SearchBlock/SearchBlock'
import MessagePost from "../MessagePost/MessagePost";
import Status from '../Status/Status'
import Modal from "../Templates/Modal/Modal";
import './Topbar.css'


export default function Topbar() {
  const localUser = JSON.parse(localStorage.getItem('user'))
  const [countAll_users, setCountAll_users] = useState(0)
  const [countWorked_today, setCountWorked_today] = useState(0)
  const [countWorked_yesterday, setCountWorked_yesterday] = useState(0)
  const [countSickLeave_today, setCountSickLeave_today] = useState(0)
  const [countSick_yesterday, setCountSick_yesterday] = useState(0)
  const [countVacation_today, setCountVacation_today] = useState(0)
  const [countVacation_yesterday, setCountVacation_yesterday] = useState(0)
  const [statisticsWork, setStatisticsWork] = useState([])
  const departId = useParams().username
  const username=useParams()

  useEffect(() => {

    const fetchUser = async () => {
      const res = await axios.get('/api/user/')
      setNotificationCount(res.data)
      console.log('COUNT=',notificationCount.length)
      res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      let filterUsers = filterUser(res.data)
      setAllUsers(filterUsers)
    }

    const fetchUserName = async () => {
      const res = await axios.get('/api/user/' + localUser._id)
      setUser(res.data)
    }

    const count = async () => {
      const getUsers = {
        id: localUser._id
      }
      if (localUser.isAdmin === true) {
        const res = await axios.post('/api/count', getUsers)
        setCountAll_users(res.data[0]['get_statistics'][0]['all_users'])
        setCountWorked_today(res.data[0]['get_statistics'][0]['not_working_today'])
        setCountWorked_yesterday(res.data[0]['get_statistics'][0]['not_worked_yesterday'])
        setCountSickLeave_today(res.data[0]['get_statistics'][0]['sick_today'])
        setCountSick_yesterday(res.data[0]['get_statistics'][0]['sick_yesterday'])
        setCountVacation_today(res.data[0]['get_statistics'][0]['vacation_today'])
        setCountVacation_yesterday(res.data[0]['get_statistics'][0]['vacation_yesterday'])
      }
