import Topbar from "../Topbar/Topbar";
import React, {useEffect, useRef, useState} from "react";
import {Link} from "react-router-dom";
import './VacanciesAdd.css'
import axios from "axios";

function VacanciesAdd() {
    const [deps, setDeps] = useState([])
    const [user, setUser] = useState([])
    const DepartmentRef = useRef()
    const StatusRef = useRef()
    const CodeVacations = useRef()
    const Company = useRef()
    const Users = useRef()
    const CodeProject = useRef()
    const Grade = useRef()

    useEffect(() => {
        const getDeps = async () => {
            const deps = await axios.get('/api/departments/')
            setDeps(deps.data)
            const user = await axios.get('/api/user/')
            setUser(user.data)

        }

        getDeps()
    }, [])


    const clickHandler = () => {
        const xx = DepartmentRef.current.value
        const xz = StatusRef.current.value
        const xxx = CodeVacations.current.value
        const xxxx = CodeProject.current.value
        const xxxxx = Company.current.value
        const xxxxxx = Users.current.value
        const xxxxxxx = Grade.current.value
        console.log('log=',xx,xx,xxx,xxxx,xxxxx,xxxxxx,xxxxxxx,xz)

    };

    const clear = () => {
        DepartmentRef.current.value = ''
    }
    
    return (
        <>
            <Topbar/>
            <div className='VacationsAdd'>
                <div className="VacationsContainerAdd">
                    <form>
                    <br/>
                    <div className="VacationsMainAdd">
                        <h2 style={{textAlign:'center'}}>Добавить вакансию</h2>
                        <div>

                            <select ref={DepartmentRef} className='SelectVacation'>
                                <option value='' >Команда</option>
                                {deps.map((dep, name) => <option key={name}
                                                                 value={dep.id} >{dep.department_name}</option>)}
                            </select>
                        </div>
                        <div>
                        <select ref={StatusRef} className='SelectVacation'>
                            <option value=''>Статус</option>
                            {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
                        </select>
                        </div>
                        <div>
                            <input ref={CodeVacations} type='text' placeholder='Код вакансии' className='InputVacation'/>
                        </div>
                        <div>
                            <select ref={Company} className='SelectVacation'>
                                <option value=''>Компания</option>
                                {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
                            </select>
                        </div>
                        <div>
                            <select ref={Users} className='SelectVacation'>
                                <option value=''>Сотрудник</option>
                                {user.map((user, name) => <option key={name} value={user.id}>{user.user_name}</option>)}
                            </select>
                        </div>
                        <div>
                            <input ref={CodeProject} type='text' placeholder='Код проекта' className='InputVacation'/>
                        </div>
                        <div>
                            <input ref={Grade} type='text' placeholder='Грейд' className='InputVacation'/>
                        </div>
                        <div style={{marginTop:'20px'}}>
                        <label style={{paddingRight:'10px'}}>Нужна вакансия после завершения проекта?</label>
                        <label htmlFor='Vacation'>
                            <input
                            type='radio'
                            value={true}
                            id='Vacation'
                            name='Vacation'

                        />Да</label>
                        <label htmlFor='Vacation1'>
                            <input
                            type='radio'
                            value={false}
                            id='Vacation'
                            name='Vacation'
                        />Нет</label>
                        </div>
                        <div style={{textAlign:'center',paddingTop:"20px"}}>
                            <button className="filterButton" onClick={clickHandler}>Сохранить</button>
                            <button className="filterButton" onClick={clear}>Сбросить</button>
                        </div>
                    </div>
                    </form>
                </div>
            </div>

        </>

    )
}
export default VacanciesAdd
