import { useEffect, useState } from 'react'
import axios from 'axios'

import Employee from '../Employee/Employee'
import './Staff.css'
import { useParams } from 'react-router-dom'
import Modal from '../Templates/Modal/Modal'
import Reports from "../../pages/Reports/Reports";
import RepostsInfo from "../ReportsInfo/RepostsInfo";
export default function Staff() {

console.log(document.location.href)
  const [statisticsWork, setStatisticsWork] = useState([])
  const [work, setwork] = useState([])
  const [users, setUsers] = useState([])
  const [count, setCount] = useState([])
  const [user, setUser] = useState([])
  const [getUser, setGetUser] = useState([])
  const [deps, setDeps] = useState([])
  const [isOpen, setIsOpen] = useState(false)
  const [isFetching, setIsFetching] = useState(false)
  const [isJob, setIsJob] = useState(true)
  const [modalActive, setModalActive] = useState(false)
  const [modalMain, setModalMain] = useState(false)
  const changedArr = []
  const [isModalFetching, setIsModalFetching] = useState(false)
  const departId = useParams().username
  const localUser = JSON.parse(localStorage.getItem('user'))
  const depsId = useParams().username

  for (let i = 0; i < localUser.checkedDeps?.length; i++) {
    changedArr.push(localUser.checkedDeps[i])

  }

  // ставит галочки в модалке
  function addChecked(deps) {
    for (let i = 0; i < deps.length; i++) {
      for (let j = 0; j < localUser.checkedDeps?.length; j++) {
        if (deps[i]._id === localUser.checkedDeps[j]) {
          deps[i].checked = true
        }
      }
    }
  }

  // фильтр нужных департаментов
  const currentDep = deps.filter(dep => departId ? dep._id === departId : dep._id === localUser.departmentId)
  let filterDeps = deps.filter(dep => currentDep.find(i => i === dep.dep_id || i === currentDep[0].dep_id))
  filterDeps.push(...currentDep)

  // фильтр нужных людей
  const usersFilter = users.filter(user => filterDeps.find(f => f._id === user.departmentId))
  const usersFilterDeps = users.filter(user => localUser.checkedDeps ?.find(f => f === user.departmentId))
 /* const depsFilterDeps = deps.filter(dep => localUser.checkedDeps ?.find(f => f === dep._id))*/

  const isLockDeps = localUser.checkedDeps !== null && localUser.checkedDeps?.length > 0

  let addChildrenArr = []

  useEffect(() => {

    // получение всех пользователей
    const fetchUsers = async () => {
      setIsFetching(true)
        const res = await axios.get('/api/user')
        res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
        let filterUsers = filterUsersFoo(res.data)
        setUsers(filterUsers)
      setIsFetching(false)
    }


    // получение данных о себе


    // получение и фильтр департаментов

    const get_users_by_users = async () => {
      setIsFetching(true)
      const getUsers={
        id:localUser._id
      }
      const res = await axios.post('/api/get_users_by_users', getUsers)
      const users_users = res.data[0]['get_users_by_users']
      users_users.sort((a, b) => a.user_name.localeCompare(b.user_name))
      let filterUsers = filterUsersFoo(users_users)
      setUsers(filterUsers)
      setIsFetching(false)

    }
    fetchUsers()
    setIsOpen(false)

    if(user.main_department===departId) {
      setUsers([])
      get_users_by_users()
    }   else{
      setGetUser([])
      fetchUsers()
    }

    const get_statistics_all_users = async () => {
      setIsFetching(true)
      if(localUser.isAdmin===true) {
        const getUsers = {
          id: localUser._id
        }
        const res = await axios.post('/api/get_statistics_all_users', getUsers)
        if (res.data[0].get_statistics_all_users[0]['all_users'] === null && !!~document.location.href.indexOf('/all_users/')) {
          alert('Нет не одного пользователя в работе')

        }
        if (res.data[0].get_statistics_all_users[0]['not_working_today'] === null && !!~document.location.href.indexOf('/not_working_today/')) {
          alert('Сегодня все в работе')
          window.location.href = `/department/${localUser.main_department}`
        }
          if (res.data[0].get_statistics_all_users[0]['not_worked_yesterday'] === null && !!~document.location.href.indexOf('/not_worked_yesterday/')) {
            alert('Вчера все работали')
            window.location.href=`/department/${localUser.main_department}`
        }
        if (res.data[0].get_statistics_all_users[0]['sick_today'] === null && !!~document.location.href.indexOf('/sick_leave_today/')) {
          alert('Нет болеющих сегодня')
          window.location.href=`/department/${localUser.main_department}`
        }

        if (res.data[0].get_statistics_all_users[0]['sick_yesterday'] === null && !!~document.location.href.indexOf('/sick_leave_yesterday')) {
          alert('Вчера не болели')
          window.location.href=`/department/${localUser.main_department}`
        }
        if (res.data[0].get_statistics_all_users[0]['vacation_today'] === null && !!~document.location.href.indexOf('/vacation_today')) {
          alert('На сегодня нет в отпуске')
          window.location.href=`/department/${localUser.main_department}`
        }
        if (res.data[0].get_statistics_all_users[0]['vacation_yesterday'] === null && !!~document.location.href.indexOf('/vacation_yesterday')) {
          alert('Вчера нет в отпуске')
          window.location.href=`/department/${localUser.main_department}`
        }

        if (!!~document.location.href.indexOf('/all_users/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['all_users'])
        }

        if (!!~document.location.href.indexOf('/not_working_today/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['not_working_today'])
        }

        if (!!~document.location.href.indexOf('/not_worked_yesterday/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['not_worked_yesterday'])
        }
        if (!!~document.location.href.indexOf('/sick_leave_today/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['sick_today'])
        }
        if (!!~document.location.href.indexOf('/sick_leave_yesterday/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['sick_yesterday'])
        }
        if (!!~document.location.href.indexOf('/vacation_today/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['vacation_today'])
        }
        if (!!~document.location.href.indexOf('/vacation_yesterday/')) {
          setStatisticsWork(res.data[0].get_statistics_all_users[0]['vacation_yesterday'])
        }

      }
      setIsFetching(false)
    }
    get_statistics_all_users()
  }, [departId])


  // фильтр пользователей по статусу
  function filterUsersFoo(users) {
    return users.filter(user => user.status === 1)
        .concat(users.filter(user => user.status === 2))
        .concat(users.filter(user => user.status === 3))
        .concat(users.filter(user => user.status !== 1 && user.status !== 2 && user.status !== 3 && user.status !== 4))
        .concat(users.filter(user => user.status === 4))
  }


  // добавляем/удаляем департамент в модалке
  const handlePush = e => {
    if (e.target.checked) {
      changedArr.push(e.target.value)
    } else {
      const index = changedArr.indexOf(e.target.value)
      changedArr.splice(index, 1)
      delete e.target.checked
    }
  }

  // добавление дочерних
  const handleAddChildren = e => {
    global.chek = e.target.checked
  }

    // обновляем данные модального окна
  const handleModalClick = async (e) => {
    e.preventDefault()
  if (global.chek===true) {
      const info={
        userId:localUser._id,
        deps:depsId,
        see_child:true
      }

    const oldItems = JSON.parse(localStorage.getItem('user'))

    const newInfo = {
      _id: oldItems._id,
      isAdmin: localUser.isAdmin,
      username: localUser.username,
      email: localUser.email,
      departmentId: localUser.departmentId,
      main_department: departId,
      status:oldItems.status
    }

    get_users_by_dep(depsId)

    localStorage.clear()
    localStorage.setItem('user', JSON.stringify({...newInfo}))

    await axios.post('/api/child/',info)
    setIsOpen(true)
    setModalMain(false)
    } else {

    const oldItems = JSON.parse(localStorage.getItem('user'))
    const newInfo = {
      _id: oldItems._id,
      isAdmin: localUser.isAdmin,
      username: localUser.username,
      email: localUser.email,
      departmentId: localUser.departmentId,
      main_department: departId,
      status:oldItems.status
    }
      const info ={
        userId:localUser._id,
        see_child:false,
        deps:depsId
      }

    setIsOpen(false)
    setModalMain(false)
    localStorage.clear()
    localStorage.setItem('user', JSON.stringify({...newInfo}))
      await axios.post('/api/child/',info)
    window.location.reload()
    }
  }

  /*  let newInfo

    if (departId) {
      addChildrenArr = addChildrenArr.map(i => i.id)
      newInfo = {
        userId: user.id,
        checkedDeps: [departId, ...addChildrenArr]
      }
    } else {
      newInfo = {
        userId: user._id,
        checkedDeps: changedArr
      }
    }

    try {
      await axios.put('/api/user/' + localUser._id, newInfo)
      if (user._id === localUser._id) {
        localStorage.setItem('user', JSON.stringify({...user, ...newInfo}))
      }
      window.location.reload()
    } catch (error) {
      console.log(error);

    }
  }*/

 // функция get_users_by_dep
  const get_users_by_dep = async () => {
    if (isJob) {
      const info = {
        depsId: depsId
      }
      const res = await axios.post('/api/get_users_by_dep/', info)
      const dep_users = res.data[0]['get_users_by_dep']
      if (dep_users === null) {

      } else {
        console.log("get_users_by_dep=",dep_users)
        dep_users.sort((a, b) => a.user_name.localeCompare(b.user_name))
        let filterUsers = filterUsersFoo(dep_users)
        setUsers(filterUsers)
        setIsOpen(true)
        setIsJob(!isJob)
      }
    }
  }



  return (
    <div className='staff'>
      {/*{!departId && <div className='changeDepsContainer'>
        {<div><span className='changeDeps' onClick={handleClick}>Изменить</span></div>}
      </div>}*/}
      {departId && <>
        {isJob ? <button onClick={get_users_by_dep} className="btnStaff" style={{width:'155px'}}>Показать дочерние</button> : <button onClick={() => window.location.href = `/department/${departId}`} className="btnStaff" >Скрыть дочерние</button> }
        <button onClick={() => setModalMain(true)} className='btnStaff' style={{float: 'right'}}>Сделать главной</button>
      </>}

      <div className="employeeWrapper">

        {isFetching
            ? <div className='isFetching'>Загрузка...</div>
            : users.length === 0 && getUser.length !==0
                ? ''
                : !!departId
                    ? !isOpen
                        ? users.filter(user => user.department_id === departId).map((user, id) => <Employee key={id} user={user}/>)
                            : usersFilter.map((user, id) => <Employee key={id} user={user} />)
          : isLockDeps
          ? usersFilterDeps.map((user, id) => <Employee key={id} user={user}/>)
          : statisticsWork.map((user, id) => <Employee key={id} user={user}/>)
        }
        {
          !!isOpen
              ?
              users.map((user, id) =>  <Employee key={id} user={user}/>)
              :""
        }

        {
          !!getUser
              ?
              getUser.map((user, id) =>  <Employee key={id} user={user}/>)
              :""
        }


      </div>
      <Modal active={modalActive} setActive={setModalActive}>
        <h1>Изменение информации:</h1>
        <hr/>
        <form className="modalLoginBox">
          {isModalFetching
            ?<div>Загрузка</div>
            : deps.map((dep, id) => <label key={id} htmlFor={dep.department_name} style={{paddingLeft: `${dep.margin}px`}}>
            <input
              type='checkbox'
              value={dep.id}
              id={dep.id}
              name={dep.department_name}
              onChange={handlePush}
              checked={dep.checked}
            />{dep.department_name}</label>)
          }
          <button className="ModalButton" type="submit" disabled={isFetching} onClick={handleModalClick}>
            Изменить
          </button>
        </form>
      </Modal>
      <Modal active={modalMain} setActive={setModalMain}>
        <h1>Изменение информации:</h1>
        <hr/>
        <form className="modalLoginBox">
          <label>
            <input
              type='checkbox'
              onChange={handleAddChildren}
            />Добавить дочерние</label>
          <button className="ModalButton" type="submit" disabled={isFetching} onClick={handleModalClick}>
            Изменить
          </button>
        </form>
      </Modal>
    </div>
  )
}
