import React, {useState} from 'react';
import Modal from "../../Templates/Modal/Modal";
import './Skills.css'

const Skills = ({ modalActiveSkills, setModalActiveSkills }) => {
    const [selectedNumber, setSelectedNumber] = useState(null);

    const handleClick = (number) => {
        setSelectedNumber(number);
        console.log(`You clicked number ${number}`);
    };
    return (

        <div>
            <Modal active={modalActiveSkills} setActive={setModalActiveSkills}>
                <h1>Скиллы:</h1>
                <br/>

                <div className='modalError'>

                </div>

                <hr/>
                <br/>
                <div className='flex'>
                    <div>
                        <div>
                            <label style={{fontWeight: "bold"}}>Выберите умение:</label>
                        </div>
                        <div>
                            <input/>
                        </div>
                    </div>

                    <div style={{width: '50px'}}></div>

                    <div>
                        <div>
                            <label style={{fontWeight: "bold"}}>Оценка:</label>
                        </div>
                        <div>
                            <div className='MainContainer'>
                                {[1, 2, 3, 4, 5].map((number) => (
                                    <div className={`WrapperContainer ${selectedNumber === number ? 'selected' : ''}`}
                                        key={number}
                                        onClick={() => handleClick(number)}
                                    >
                                        {number}
                                    </div>
                                ))}
                            </div>
                        </div>

                    </div>

                </div>
                <br/>
                <button className="ModalButton"  type="submit">
                    Добавить умение
                </button>
                <br/>
                <br/>
                <hr/>

                <div className='divreserve'>

                </div>
                <br/>
                <br/>
                <button  className="ModalButton">Отменить все</button>
                <br/><br/>
            </Modal>
        </div>
    );
};

export default Skills;
