import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = () => {
    const [dep, setDep] = useState([]);

    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            if (res.data[0].department_tree_to_json.length === 0) {
                setDep([]);
            } else {
                setDep(res.data);
                // Найти и открыть родительские узлы до id 21
                openParentsUntilId(21, res.data);
            }
        };
        getDeps();
    }, []);

    const openParentsUntilId = (targetId, nodes) => {
        nodes.forEach(node => {
            if (node.department_tree_to_json.id === targetId) {
                // Отметить этот узел как открытый
                node.isOpen = true;
                // Рекурсивно отметить открытыми всех его родителей
                markParentsOpen(node);
            } else if (node.department_tree_to_json.Subclasses) {
                openParentsUntilId(targetId, node.department_tree_to_json.Subclasses);
            }
        });
    };

    const markParentsOpen = (node) => {
        if (node && node.parent) {
            node.parent.isOpen = true;
            markParentsOpen(node.parent);
        }
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                {dep.length >= 1 ? (
                    <div className="tree">
                        <div className="treeWrapper">
                            {dep.map((node, i) => (
                                <TreeView
                                    key={node.department_tree_to_json.Name + '|' + i}
                                    nodeLabel={(
                                        <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                            <span className="node">{node.department_tree_to_json.Name}</span>
                                        </Link>
                                    )}
                                    defaultCollapsed={!node.isOpen}
                                >
                                    {node.department_tree_to_json.Subclasses && node.department_tree_to_json.Subclasses.map((departments) => (
                                        <TreeView
                                            key={departments.id}
                                            nodeLabel={(
                                                <Link to={`/department/${departments.id}`} className='depsUser'>
                                                    <span className="node">{departments.Name}</span>
                                                </Link>
                                            )}
                                            defaultCollapsed={!departments.isOpen}
                                        >
                                            {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                <TreeView
                                                    key={departments1.id}
                                                    nodeLabel={(
                                                        <Link to={`/department/${departments1.id}`} className='depsUser'>
                                                            <span className="node">{departments1.Name}</span>
                                                        </Link>
                                                    )}
                                                    defaultCollapsed={!departments1.isOpen}
                                                >
                                                    {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                        <Link to={`/department/${departments2.id}`} className='depsUser' key={departments2.id}>
                                                            <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            ))}
                        </div>
                    </div>
                ) : ''}
            </div>
            <div>
            </div>
        </div>
    );
};
export default Sidebar;
