import {Link} from 'react-router-dom'

import './SearchBlock.css'
import {useEffect, useState} from "react";
import axios from "axios";

const usersStatuses = {0: '', 1: 'start', 2: 'end', 3: 'sick_leave', 4: 'deleted' ,5: 'vacation', 6: 'time_off', 7: 'other'}
function findStatus(k){
    for(let i in usersStatuses){
        if(i == k){
            return usersStatuses[i]
        }
    }
}


export default function SearchBlock({user}) {
    const [admin, setAdmin] = useState([''])
    const [dateStart, setDateStar] = useState(null)
    const [dateEnd, setDateEn] = useState(null)

    useEffect(() => {
        const fetchUser = async () => {
            const res = await axios.get('/api/vacation?id='+user.id)
            console.log("RES.DATA=",res.data)

            if(res.data.length===0){
                setDateStar(global.workdate='')
                setDateEn(global.worked='')
            }else{
                global.workdate=res.data[0].workdate
                global.worked=res.data[0].worked
                setDateStar(global.workdate)
                setDateEn(global.worked)
            }
            const users = await axios.get('/api/user/'+user.id)


            if(users.data.isadmin===true){
                setAdmin('*')}else{
                setAdmin('')
            }}

        if(user.status===3){
            fetchUser()}

        if(user.status===7){
            fetchUser()
        }

        if(user.status===1){
            fetchUser()}

        if(user.status===6){
            fetchUser()}

        if(user.status===5){
            fetchUser()
        }
        if(user.status===2){
            fetchUser()


        }}, [user._id])

    const localUser = JSON.parse(localStorage.getItem('user'))
    const name=user.email.toLowerCase()
    return (
        <div className="searchBlockContainer">
            {user.status === 4 && !localUser.isAdmin ? <div></div> :
                <div className={'employee ' + (findStatus(user.status))}>
                    <div className='top'>{localUser.isAdmin ?
                        <Link to={`/profile/${user.id}`} onClick={() => window.location.href = `/profile/${user.id}`}
                              className='name'>
                            <div className='name'>{user.user_name}{admin}</div>
                        </Link> : <div className='name'><span>{user.user_name}{admin}</span></div>
                    }
                    </div>
                    {/*{<div className='department'>{user.department_id}</div>}*/}
                    <div className='email'>{name.charAt(0).toUpperCase() + name.slice(1)}</div>
                    {user.status === 5 && <div className='vacationText'>Отпуск:{global.workdate}-{global.worked}</div>}
                    {user.status === 6 && <div className='vacationText'>Отгул:{global.workdate}-{global.worked}</div>}
                    {user.status === 7 && <div className='vacationText'>Другая причина:{global.workdate}-{global.worked}</div>}
                    {user.status === 3 && <div className='vacationText'>Больничный:{global.workdate}-{global.worked}</div>}

                </div>
            }
        </div>
    )
}
