import React from 'react'
import { Link, useParams } from 'react-router-dom'
import axios from 'axios'
import { useEffect, useState } from 'react'

import SidebarDeps from '../SidebarDeps/SidebarDeps'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'

export default function Sidebar() {
  const [deps, setDeps] = useState([])
  const [users, setUsers] = useState([])
  const [isFetching, setIsFetching] = useState(false)
  const [filterDeps, setFilterDeps] = useState([])
  const currentUrl = useParams().username
  const localUser = JSON.parse(localStorage.getItem('user'))

    useEffect(() => {
        const getDeps = async () => {
           const res = await axios.get('/api/department_tree_to_json/')
            console.log("res=",res)
        }
        getDeps()
        }, [])


  const dataSource = [

      {
          type: 'Управление соппровождения и эксплуатации систем подержки бизнеса',id:1,
          child: [
              {name: 'Служба поддержски продуктовых систем розничного бизнеса',id:2,
                  child: [
                      {name: 'Отдел администрирования бэк-офисных систем розничного бизнеса',id:3},
                      {name: 'Отдел круглосуточной поддержски процессов закрытия операционного дня отчетности розничного бизнеса', collapsed: false,id:4},
                      {name: 'Отдел сопровождения активных операций ФЛ',id:5},
                      {name: 'Отдел сопровождения пассивных операций ФЛ',id:6}]},

              {name: 'Служба поддержски централизованных и технологических процессов',id:7,
                  child: [
                      {name: 'Отдел поддержски обмена с ФОИВ',id:8},
                      {name: 'Отдел поддержски систем электронного хранения', collapsed: false,id:9},
                      {name: 'Отдел поддержски технологиеских процессов',id:10,
                          child: [
                              {name:'Группа поддержки зарплатных проектов',id:11,collapsed: false},
                              {name:'Группа поддержки логичесих карт и технологических сервисов',id:12, collapsed: false}
                          ]},
                      {name: 'Отдел сопровождения централизованных процессов',id:13}
                  ],
              },

              {name: 'Служба сопровождения муниципальных и специальных сервисов', collapsed: false,id:14,
                  child: [
                      {name: 'Отдел сопровождения муниципальных проектов', collapsed: false,id:15},
                      {name: 'Отдел сопровождения специальных сервисов', collapsed: false,id:16}
                  ],
              },

              {name: 'Служба сопровождения операционной и расчетной деятельности', collapsed: false,id:17,
                  child: [{name: 'Отдел поддержки пользователей прочих АБС', collapsed: false,id:18},
                      {name: 'Отдел систем доставки электронных документов',id:19,
                          child: [
                              {name:'Группа поддежрски SWIFT/КФТ/TELEX',id:20}]},
                      {name: 'Отдел сопровождения АБС ЮЛ, систем расчетов и задач Казначейства',id:21,
                          child: [
                              {name:'Группа интеграции с внешними системами',id:22,collapsed: false},
                              {name:'Группа поддержки пользователей восточной зоны РФ',id:23,collapsed: false},
                              {name:'Группа поддержки пользователей европейской зоны РФ',id:24,collapsed: false},
                              {name:'Группа сопровождения систем расчетов',id:25}]},
                      {name: 'Отдел сопровождения операционного дня', collapsed: false,id:26},
                      {name: 'Отдел специального технологического сопровождения АБС', collapsed: false,id:27},
                      {name: 'Отдел управления изменениями', collapsed: false,id:28}

                  ]
              }
          ],
      },
  ]
console.log("dataSource=",dataSource)


  // загрузка всех департаментов
  useEffect(() => {
    // сортировка на дочерние и тд
    const getDeps = async () => {
        setIsFetching(true)
        const res = await axios.get('/api/departments/')


        for(let i = 0; i < res.data.length-1; i++){
          for(let j = i+1; j < res.data.length; j++){
            if(res.data.find(k => k === res.data[j].id && k !== res.data[i].id)){
              if(res.data[j].margin ){
                res.data[j].margin += 40
              } else {
                res.data[j].margin = 40
              }
            }
          }
        }

      let newArr = res.data.filter(d => d.id !== 7)
      newArr.push(res.data[6])
      setFilterDeps(newArr.filter(a => a.dep_id === 1))
      setDeps(newArr)
      if(currentUrl && currentUrl !== localUser._id){
        const arr1 = [1, 2, 3, 4, 5, 6, 7, '61efa2f6cf0ce39b27ba2a53', '61efa304cf0ce39b27ba2a54', '61efa31dcf0ce39b27ba2a55', '61efa32bcf0ce39b27ba2a56', '61efa33acf0ce39b27ba2a57', '61efa344cf0ce39b27ba2a58', '61efa34fcf0ce39b27ba2a59']
        const arr2 = [6, 8, 9, 10, 11, '61efa344cf0ce39b27ba2a58', '61efa35dcf0ce39b27ba2a5a', '61efa361cf0ce39b27ba2a5b', '61efa36acf0ce39b27ba2a5c', '61efa374cf0ce39b27ba2a5d']

        if(arr1.find(i => i === currentUrl)){
          setFilterDeps(newArr.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
        }
        if(arr2.find(i => i === currentUrl)){
          setFilterDeps(newArr)
        }else{
          setFilterDeps(newArr.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
        }
      }
      setIsFetching(false)
    }

    // загрузка пользователей и сортировка по департаментам
    const fetchUser = async () => {
      setIsFetching(true)
      const res = await axios.get('/api/user/')
      const id=res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      setUsers(res.data)
      setIsFetching(false)
      console.log("ID=",id)
    }

    fetchUser()
    getDeps()
  }, [])

  const isShowDeps = (e) => {
    const arr1 = [1, 2, 3, 4, 5, 6, 7, '61efa2f6cf0ce39b27ba2a53', '61efa304cf0ce39b27ba2a54', '61efa31dcf0ce39b27ba2a55', '61efa32bcf0ce39b27ba2a56', '61efa33acf0ce39b27ba2a57', '61efa344cf0ce39b27ba2a58', '61efa34fcf0ce39b27ba2a59']
    const arr2 = [6, 8, 9, 10, 11, '61efa344cf0ce39b27ba2a58', '61efa35dcf0ce39b27ba2a5a', '61efa361cf0ce39b27ba2a5b', '61efa36acf0ce39b27ba2a5c', '61efa374cf0ce39b27ba2a5d']

    if(e.target.value === 1){
      setFilterDeps(deps.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
    }
    if(arr2.find(i => i === e.target.value)){
      setFilterDeps(deps)
    }else{
      setFilterDeps(deps.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
    }
  }

  return (
    <div className='sidebar'>
      <div className="sidebarWrapper">
        <div className="tree">
          <div className="treeWrapper">

              {dataSource.map((node, i) => {

                  const label = <Link to={`/department/${node.id}`} className='depsUser' style={{fontWeight: `bold`}}><span className="node">{node.type}</span></Link>

                  return (

                      <TreeView key={node.type + '|' + i} nodeLabel={label} defaultCollapsed={true}>

                          {node.child.map(departments => {

                              const label2 = <Link to={`/department/${departments.id}`} className='depsUser'><span className="node">{departments.name}</span></Link>

                              return (

                                  <TreeView nodeLabel={label2} key={departments.child} defaultCollapsed={true}>

                                      {!!departments.child ? departments.child.map(departments1  => {

                                          const label3 = <Link to={`/department/${departments1.id}`} className='depsUser'><span className="node">{departments1.name}</span></Link>

                                          return(

                                              <TreeView nodeLabel={label3} defaultCollapsed={true} >

                                                  {!!departments1.child ? departments1.child.map(departments2  => {

                                            return(

                                                <Link to={`/department/${departments2.id}`} className='depsUser'>

                                                    <div  style={{paddingBottom:'8px'}}>{departments2.name}</div>

                                                </Link>
                                            )

                                      }):''}
                                  </TreeView>
                              );
                          }):''}
                      </TreeView>
                  );
              })}
                      </TreeView>
                  );
              })}
          </div>
        </div>
      </div>
        {/*{ <SidebarDeps users={users} dep={deps}/>}*/}
        <div>

        </div>
    </div>
  )
}
