import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Sidebar.css';
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css';

const Sidebar = ({ initialOpenSubclassId }) => {
    const [dep, setDep] = useState([]);
    const [openSubclassId, setOpenSubclassId] = useState(17);

    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            setDep(res.data);
        };
        getDeps();
    }, []);

    useEffect(() => {
        setOpenSubclassId(initialOpenSubclassId);
    }, [initialOpenSubclassId]);

    const handleSubclassClick = (id) => {
        setOpenSubclassId(id);
    };

    const renderTree = (node) => {
        const label = (
            <Link to={`/department/${node.department_tree_to_json.id}`} className="level1">
                <span className="node">{node.department_tree_to_json.Name}</span>
            </Link>
        );

        return (
            <TreeView
                key={node.department_tree_to_json.id}
                nodeLabel={label}
                defaultCollapsed={true}
            >
                {node.department_tree_to_json.Subclasses.map((subclass) => {
                    return (
                        <TreeView
                            key={subclass.id}
                            nodeLabel={
                                <div className="depsUser" onClick={() => handleSubclassClick(subclass.id)}>
                                    <span className="node">{subclass.Name}</span>
                                </div>
                            }
                            defaultCollapsed={true}
                            collapsed={subclass.id !== openSubclassId}
                        >
                            {subclass.Subclasses && subclass.Subclasses.map((subSubclass) => {
                                return (
                                    <Link to={`/department/${subSubclass.id}`} className="depsUser" key={subSubclass.id}>
                                        <div style={{ paddingBottom: '8px' }}>{subSubclass.Name}</div>
                                    </Link>
                                );
                            })}
                        </TreeView>
                    );
                })}
            </TreeView>
        );
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map((node) => {
                            return renderTree(node);
                        })}
                    </div>
                </div>
            </div>
            <div></div>
        </div>
    );
};

export default Sidebar;
