import React, {useEffect, useRef, useState} from 'react'
import Topbar from '../Topbar/Topbar'
import {Link} from "react-router-dom";
import './VacanciesView.css'
import axios from "axios";
import MessagePost from "../MessagePost/MessagePost";

function VacanciesView() {

    const [deps, setDeps] = useState([])
    const [users, setUsers] = useState([])
    const department = useRef()
    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/departments/')
            setDeps(res.data)
        }

        getDeps()
    }, [])

    const button = async () => {
        const res = await axios.get('/api/user/')
        setUsers(res.data)
        console.log('res=',res.data)
    }


    return (
        <>
            <Topbar/>
            <div className='Vacations'>
                <div className="VacationsContainer">
                    <h2>Вакансии</h2>
                    <br/>
                    <div className="VacationsMain">
                        <div>
                    <input
                        type='search'
                        placeholder="Наименование вакансии"
                        className="ModalInput"
                        style={{'marginRight': '10px'}}
                    />
                    </div>
                        <div>
                        <select ref={department} className='deps'>
                            <option value=''>Выберите подразделение</option>
                            {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
                        </select>
                        </div>
                            <div>
                        <button className="filterButton" onClick={button}>Найти</button>
                        <button className="filterButton" >Сбросить</button>
                            </div>
                        </div>
                    <br/>

                   <div>
                    <input
                        type='date'
                        placeholder="Выберите дату"
                        className="ModalInput"

                        style={{'marginRight': '10px'}}
                    />
                        —  <input
                            type='date'
                            placeholder="Выберите дату"
                            className="ModalInput"
                            style={{'marginRight': '10px'}}
                        />
                    </div>
                </div>

                    <div>
                        {users.map((user, id) => (
                            <div className='VacationsContainer' key={id}>{user.user_name}{user.nominations_id}</div>

                        ))}
                </div>
            </div>
        </>
    )
}
export default VacanciesView
