import axios from 'axios'
import { useEffect, useRef, useState } from 'react'
import { Create, Delete } from '@material-ui/icons'
import Modal from '../Templates/Modal/Modal'
import './ProfileInfo.css'
import {useParams} from "react-router-dom";
export default function ProfileInfo() {
  const [targetChecked, setTargetChecked] = useState(false)
  const [checked, setCheckedValue] = useState()
  const [modalUser, setModalUser] = useState()
  const [count, setCount] = useState(0)
  const [userVote,setUserVote] = useState([])
  const [depsName,setDepsName] = useState([])
  const [user, setUser] = useState([])
  const [isFetching, setIsFetching] = useState(false)
  const [modalActive, setModalActive] = useState(false)
  const [modalActivePass, setModalActivePass] = useState(false)
  const [modalActiveVote, setModalActiveVote] = useState(false)
  const [vote, setVote]=useState([])
  const [modalActiveResetPass, setModalActiveResetPass] = useState(false)
  const [modalActiveReset, setModalActiveReset] = useState(false)
  const [modalActiveDelete, setModalActiveDelete] = useState(false)
  const [deps, setDeps] = useState([])
  const [error, setError] = useState('')
  const [er, setErr] = useState('')
  const username=useParams()
  let localUser = !!username ? {_id:username.username,email:""} : JSON.parse(localStorage.getItem('user'))
  const localUse = JSON.parse(localStorage.getItem('user'))

  const modalName = useRef()
  const modalDep = useRef()
  const modalVote = useRef()
  const modalEmail = useRef()
  const modalOldPass = useRef()
  const modalNewPass = useRef()
  const modalNewPassAgain = useRef()

  const usersStatuses = {0: '', 1: 'start', 2: 'end', 3: 'sick_leave', 4: 'deleted' ,5: 'vacation', 6: 'time_off', 7: 'other'}
  function findStatus(k){
    for(let i in usersStatuses){
      if(i == k){
        return usersStatuses[i]
      }
    }
  }

  // данные о пользователе


  // Список всех департаментов и получение департаментов по юзеру
  useEffect(() => {
    const getDeps = async () => {

      const res = await axios.get('/api/departments/')
      setDeps(res.data)
    }
    const getNameDeps = async () => {
      const deps = {
        user: username.username
      }
      const res = await axios.post('/api/departments/',deps)
      setDepsName(res.data)
    }

    const fetchUser = async () => {
      localUser._id=!!localUser._id ? localUser._id:localUser.id
      localUser._id=!!username ? username.username:localUser._id
      const res = await axios.get('/api/user/'+username.username)
      setUser(res.data)
      setIsFetching(false)
      const user = await axios.get('/api/user/')
      const sort = user.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      setUserVote(sort)
    }

// Номинации
    const fetchVote = async () => {

      const res = await axios.get('/api/vote/')
      const id = res.data.sort((a, b) => a.id.localeCompare(b.id))
      setVote(id)

    }
    fetchVote()
    fetchUser()
    getDeps()
    getNameDeps()
  }, [])







  /*  // данные о работе от последнего к первому
   useEffect(() => {
      setIsFetching(true)
      const fetchUser = async () => {
        const res = await axios.get('/api/user/')
        setPosts(res.data.sort((p1, p2) => {
          return new Date(p2.workStart) - new Date(p1.workStart)
        }))
        setIsFetching(false)
      }
      fetchUser()
    }, [username])*/





  // изменение данных пользователя
  const handleClick = async (e) => {
    e.preventDefault()
    const newInfo= {
      _id: username.username,
      username: modalName.current.value || user.username,
      email: modalEmail.current.value.toUpperCase() || user.email,
      departmentId: modalDep.current.value || user.departmentId,
    }

    try {
      await axios.put('/api/user/', newInfo)
      setModalActive(false)
      window.location.reload()
    } catch (error) {
      setError("Такой email существует")
    }
  }

  // изменить пароль
  const handleClickPassword = async (e) => {

    e.preventDefault()
    if(modalNewPass.current.value !== modalNewPassAgain.current.value){
      setErr('Новые пароли не совпадают')
      return }
    const passwords = {
      userId: localUse._id,
      password: localUse.password,
      oldPassword: modalOldPass.current.value,
      newPassword: modalNewPass.current.value
    }
    try {
      await axios.put('/api/newPassword/', passwords)
      setModalActivePass(false)
      /*      localStorage.clear()
            window.location.reload()
            window.location.href = '/'*/
    } catch (er) {
      setErr('Старые пароли не совпадают')
    }
  }

  // сбросить пароль
  const handleResetPass = async (e) => {
    e.preventDefault()

    const resetPassword = {
      userId: username.username,
      newPassword: 'qwerty123'
    }

    try {
      await axios.put('/api/resetPassword/', resetPassword)
      setModalActiveResetPass(false)
    } catch (error) {
    }
  }

  const status = async () => {
    if(user.status===1){
      const status = {
        status: 2,
        typework_id: 1,
        userId: user.id,
        healthEnd: '-',
        commentEnd: '-',
        workEnd: new Date(Date.now())
      }
      await axios.put("/api/post/", status)
    }else{
      alert("Пользователь не в работе")
    }
  }


  // удалить пользователя
  const handleDelete = async (e) => {
    e.preventDefault()
    setIsFetching(true)

    const newPost = {
      userId: username.username,
      healthEnd: 1,
      status: 4
      /*      workEnd: new Date(Date.now()),
            workTime: new Date(Date.now()) - new Date(posts[0].workStart)*/
    }
    /* localStorage.setItem('user', JSON.stringify({...localUser, status:3}))*/
    try {
      await axios.put('/api/deluser/', newPost)
      /*      if(user.status === 1){
              await axios.put("/api/post/"+posts[0]._id, newPost)
            }*/
      /*      localStorage.clear()
            window.location.reload()
            window.location.href = '/'*/
      setModalActiveDelete(false)
      window.location.href = `/profile/${user.id}`
    } catch (error) {
      console.log(error);
    }
  }
  localUser =(!!user && ! Array.isArray(user)) ? user:localUser
  localUser.username=!!localUser.user_name ? localUser.user_name : localUser.username
  const name=localUser.email.toLowerCase()


  const buttonClick  =()=> {
    setCount(count + 1)
  }

  const handlePush = e => {
    setCheckedValue(e.target.value)
    setTargetChecked(e.target.checked)
  }

  const hand = e => {
    setModalUser(e.target.value)
  }


  const clickVote = async (e) => {
    e.preventDefault()
    const voteUser = {
      user_id: localUse._id,
      vote_for_user: modalUser,
      nominations_id: checked
    }


    if(modalUser===undefined){
      setCount(count-1)
      setErr("Выберите участника")
      return
    }

    if(count<=10){
      console.log('count=',count)
      setErr('')
      await axios.post('/api/vote/', voteUser)
    }else{
      setTargetChecked(false)
      setModalActiveVote(false)
      window.location.reload()
    }
  }

  return (
      <div className='profileInfo'>
        {isFetching
            ? <div className='isFatching'>Загрузка...</div>
            : <div className={'employeePI ' + (findStatus(user.status))}>
              <div className='top'>
            <span className='name'>
              {user.user_name}
              {localUser ? <Create style={{fontSize:'medium', cursor:'pointer', marginLeft:'10px'}} onClick={() => setModalActive(true)}/> : ''}
              {localUse.isAdmin ? <Delete style={{cursor:'pointer', fontSize:'medium'}}  onClick={() => setModalActiveDelete(true)}/> : <></>}
            </span>
              </div>
              <div style={{fontSize:'18px'}} className='email'>{name.charAt(0).toUpperCase() + name.slice(1)}</div>
              <div>{depsName}</div>
              <hr className="hr"/>
              {<div className='changePass' onClick={() => setModalActivePass(true)}>Изменить пароль</div>}

              <div className='changePass' style={{width: "20px"}}></div>

              {localUse.isAdmin ? <div className='changePass' onClick={() => setModalActiveResetPass(true)}>Сбросить пароль</div>:''}

              {localUse.isAdmin ? <div className='changePass' style={{width: "20px"}}></div> :''}

              {localUse.isAdmin ? <div className='changePass' onClick={() => setModalActiveReset(true)} >Закончить</div>:''}

              {localUse.isAdmin ? <div className='changePass' style={{width: "20px"}}></div> :''}

              {<div className='changePass' onClick={() => setModalActiveVote(true)}>Проголосовать</div>}


            </div>
        }
        <Modal active={modalActive} setActive={setModalActive}>
          <h1>Изменение информации:</h1>
          <hr/>
          {error && <div className='modalError'>{error}</div>}
          <form className="modalLoginBox" onSubmit={handleClick}>
            <input
                placeholder="Name"
                className="ModalInput"
                ref={modalName}
            />
            <select ref={modalDep}>
              <option value=''>Выберите подразделение</option>
              {deps.map((dep, id) =>
                  <option key={id} value={dep.id}>{dep.department_name}</option>)}
            </select>
            <input
                placeholder="Email"
                type='emailLogin'
                className="ModalInput"
                ref={modalEmail}
                minLength='6'
            />
            <button className="ModalButton" type="submit" disabled={isFetching}>
              Изменить
            </button>
          </form>
        </Modal>
        <Modal active={modalActivePass} setActive={setModalActivePass} >
          <h1>Изменение информации:</h1>
          <hr/>
          {er && <div className='modalError'>{er}</div>}
          <form className="modalLoginBox" onSubmit={handleClickPassword}>
            <input
                placeholder="Old Password"
                type='password'
                className="ModalInput"
                ref={modalOldPass}
            />
            <input
                placeholder="New Password"
                type='password'
                className="ModalInput"
                ref={modalNewPass}
                minLength='6'
            />
            <input
                placeholder="New Password Again"
                type='password'
                className="ModalInput"
                ref={modalNewPassAgain}
                minLength='6'
            />
            <button className="ModalButton" type="submit" disabled={isFetching}>
              Изменить
            </button>
          </form>
        </Modal>
        <Modal active={modalActiveResetPass} setActive={setModalActiveResetPass}>
          <h1>Изменение информации:</h1>
          <hr/>
          <form className="modalLoginBox" onSubmit={handleResetPass}>
            <h2>Новый пароль будет: qwerty123</h2>
            <button className="ModalButton" type="submit" disabled={isFetching}>
              Сбросить
            </button>
          </form>
        </Modal>
        <Modal active={modalActiveReset} setActive={setModalActiveReset}>
          <h1>Изменение информации:</h1>
          <hr/>
          <form className="modalLoginBox" onSubmit={status}>
            <h2>Закончить время</h2>
            <button className="ModalButton" type="submit" disabled={isFetching}>
              Закончить
            </button>
          </form>
        </Modal>
        <Modal active={modalActiveDelete} setActive={setModalActiveDelete}>
          <h1>Изменение информации:</h1>
          <hr/>
          <form className="modalLoginBox" onSubmit={handleDelete}>
            <h2>Пользователь будет удален</h2>
            <button className="ModalButton" type="submit" disabled={isFetching}>
              Удалить
            </button>
          </form>
        </Modal>
        <Modal active={modalActiveVote} setActive={setModalActiveVote}>

          <h1>Голосование</h1>
          {er && <div className='modalError'>{er}</div>}
          <hr/>
          {
            vote.map(
                (isvote, id) =>

                  <label key={id} htmlFor={isvote.id} className='inputVote'>

                  <label value={isvote.id} id={isvote.id} name={isvote.id} />{isvote.nominations_name}

                    <br/>
                    {isvote.description}
                    <select onChange={handlePush} key={id} value={userVote.id} className='select'>
                  <option value=''>Выберите участника</option>
                  {userVote.map((userVote, id) => <option onChange={handlePush} key={id} value={userVote.id}>{userVote.user_name}</option>)}
                  </select>
                  </label>
            )}

          <form className="vote" onSubmit={clickVote}>
            {
                <button className="button" type="submit" disabled={isFetching}>
                  Проголосовать
                </button>
            }
          </form>
        </Modal>
      </div>
  )
}
