import './InfoPost.css'

export default function InfoPost({post, username}) {
   /*console.log(post)*/
  // перевод даты в читабельный вид
  let dateStart = new Date(post.workStart)
  let dateEnd = new Date(post.workEnd)
  let workTime = new Date(post.workTime)
  let AllTimeWork = new Date(workTime - 3*60*60*1000)
  dateStart = dateStart.toLocaleString()
  dateEnd = dateEnd.toLocaleString()
  AllTimeWork = AllTimeWork.toLocaleTimeString()
  dateStart = dateStart.split(',')
  dateEnd = dateEnd.split(',')

  return (
      <tr style={{minWidth:"100px"}}>
          <td style={{border: "1px solid grey",  minWidth:"100px"}}> {post.workdate}</td>
          <td style={{border: "1px solid blue"}}>{post.workstart}</td>
          <td style={{border: "1px solid blue"}}>{post.workend}</td>
          <td style={{border: "1px solid blue"}}>{post.worklong}</td>
          <td style={{border: "1px solid blue"}}>{post.healthstart === '0' ? 'Болен' : 'Здоров'}-{post.healthend === '1' ? 'Здоров' :post.healthend === '0' ? 'Болен' : ''}</td>

          <td style={{border: "1px solid blue"}}>{post.commentstart}-{post.commentend}</td>
      </tr>
  )
}
