import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Sidebar.css';
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css';

const Sidebar = () => {
    const [dep, setDep] = useState([]);
    const [collapsedNodes, setCollapsedNodes] = useState({});

    // загрузка всех департаментов
    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            const initialState = res.data.reduce((acc, node) => {
                acc[node.department_tree_to_json.id] = false; // Инициализируем все узлы как раскрытые
                return acc;
            }, {});
            setDep(res.data);
            setCollapsedNodes(initialState);
        };

        getDeps();
    }, []);

    // функция для обновления состояния раскрытия узла
    const toggleNode = (nodeId) => {
        setCollapsedNodes({
            ...collapsedNodes,
            [nodeId]: !collapsedNodes[nodeId],
        });
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map((node, i) => {
                            const label = (
                                <div
                                    className='level1'
                                    onClick={() => toggleNode(node.department_tree_to_json.id)} // обработчик клика по ссылке
                                >
                                    <span className="node">{node.department_tree_to_json.Name}</span>
                                </div>
                            );

                            return (
                                <TreeView
                                    key={node.department_tree_to_json.Name + '|' + i}
                                    nodeLabel={label}
                                    defaultCollapsed={false}
                                    collapsed={collapsedNodes[node.department_tree_to_json.id]} // устанавливаем состояние раскрытия узла
                                >
                                    {node.department_tree_to_json.Subclasses.map((departments) => (
                                        <TreeView
                                            key={departments.id}
                                            nodeLabel={(
                                                <Link
                                                    to={`/department/${departments.id}`}
                                                    className='depsUser'
                                                >
                                                    <span className="node" onClick={(e) => e.stopPropagation()}>{departments.Name}</span>
                                                </Link>
                                            )}
                                            defaultCollapsed={true}
                                            collapsed={collapsedNodes[departments.id]} // устанавливаем состояние раскрытия узла
                                        >
                                            {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                <TreeView
                                                    key={departments1.id}
                                                    nodeLabel={(
                                                        <Link
                                                            to={`/department/${departments1.id}`}
                                                            className='depsUser'
                                                        >
                                                            <span className="node" onClick={(e) => e.stopPropagation()}>{departments1.Name}</span>
                                                        </Link>
                                                    )}
                                                    defaultCollapsed={true}
                                                    collapsed={collapsedNodes[departments1.id]} // устанавливаем состояние раскрытия узла
                                                >
                                                    {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                        <Link
                                                            key={departments2.id}
                                                            to={`/department/${departments2.id}`}
                                                            className='depsUser'
                                                            onClick={(e) => e.stopPropagation()} // Предотвращаем всплытие клика, чтобы избежать переключения состояния вложенности
                                                        >
                                                            <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            );
                        })}
                    </div>
                </div>
            </div>
            <div></div>
        </div>
    );
};

export default Sidebar;
