import "./VcanciesItem.css"
import moment from "moment";


const VacanciesItem =({vac,departId})=> {
    const vacanciesArray = Array.isArray(vac) ? vac : [vac];
    const filteredVacancies = vacanciesArray.filter(item => item.team_id === departId);
    const date = moment().format('YYYY-MM-DD');

    return (
        <>
            {filteredVacancies.map((vacancy, index) => (
                <div key={index} className="angry-grid1">
                    <div id={`item-01-${index}`}>{vacancy.name}</div>
                    <div id={`item-11-${index}`}>{date === vacancy.create_date ? 'NEW' : ''}</div>
                    <div id={`item-21-${index}`}>{vacancy.description}</div>
                    <div id={`item-31-${index}`}></div>
                    <div id={`item-41-${index}`}>{moment(vacancy.create_date).format('DD.MM.YYYY')}</div>
                </div>
            ))}
        </>
    );
}

export default VacanciesItem
