import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = () => {
    const [dep, setDep] = useState([]);

    useEffect(() => {
        const getDeps = async () => {
            try {
                const res = await axios.get('/api/department_tree_to_json/');
                const data = res.data[0]?.department_tree_to_json || [];
                setDep(data);
            } catch (error) {
                console.error("Error fetching department data:", error);
                setDep([]);
            }
        };
        getDeps();
    }, []);

    const renderTree = (node) => (
        <TreeView
            nodeLabel={(
                <Link to={`/department/${node.id}`} className='level1'>
                    <span className="node">{node.Name}</span>
                </Link>
            )}
            key={node.id}
            defaultCollapsed={false}
        >
            {node.Subclasses?.map((subNode) => renderTree(subNode))}
        </TreeView>
    );

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                {dep.length > 0 ? (
                    <div className="tree">
                        <div className="treeWrapper">
                            {dep.map((node) => renderTree(node))}
                        </div>
                    </div>
                ) : null}
            </div>
        </div>
    );
};

export default Sidebar;
