import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'
import { useParams } from "react-router";


const Sidebar = () => {
    const { departmentId } = useParams('15'); // Получаем входящий параметр
    const [dep, setDep] = useState([]);
    const [openNodes, setOpenNodes] = useState({}); // Состояние для хранения открытых узлов

    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            if (res.data[0].department_tree_to_json.length === 0) {
                setDep([]);
            } else {
                setDep(res.data);
                const initialOpenNodes = getInitialOpenNodes(res.data, departmentId);
                setOpenNodes(initialOpenNodes); // Инициализация открытых узлов
            }
        };
        getDeps();
    }, [departmentId]);

    const getInitialOpenNodes = (data, departmentId) => {
        const openNodes = {};
        const openNodeRecursive = (nodes) => {
            nodes.forEach(node => {
                if (node.department_tree_to_json.id === departmentId || node.department_tree_to_json.Subclasses.some(sub => sub.id === departmentId)) {
                    openNodes[node.department_tree_to_json.id] = true;
                }
                if (node.department_tree_to_json.Subclasses) {
                    openNodeRecursive(node.department_tree_to_json.Subclasses);
                }
            });
        };
        openNodeRecursive(data);
        return openNodes;
    };

    const handleToggle = (id) => {
        setOpenNodes(prevOpenNodes => ({
            ...prevOpenNodes,
            [id]: !prevOpenNodes[id],
        }));
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                {dep.length >= 1 ? (
                    <div className="tree">
                        <div className="treeWrapper">
                            {dep.map((node, i) => {
                                const label = (
                                    <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                        <span className="node">{node.department_tree_to_json.Name}</span>
                                    </Link>
                                );

                                return (
                                    <TreeView
                                        key={node.department_tree_to_json.Name + '|' + i}
                                        nodeLabel={label}
                                        collapsed={!openNodes[node.department_tree_to_json.id]}
                                        onClick={() => handleToggle(node.department_tree_to_json.id)}
                                    >
                                        {node.department_tree_to_json.Subclasses.map((departments) => (
                                            <TreeView
                                                nodeLabel={(
                                                    <Link to={`/department/${departments.id}`} className='depsUser' key={departments.id}>
                                                        <span className="node">{departments.Name}</span>
                                                    </Link>
                                                )}
                                                key={departments.id}
                                                collapsed={!openNodes[departments.id]}
                                                onClick={() => handleToggle(departments.id)}
                                            >
                                                {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                    <TreeView
                                                        nodeLabel={(
                                                            <Link to={`/department/${departments1.id}`} className='depsUser' key={departments1.id}>
                                                                <span className="node">{departments1.Name}</span>
                                                            </Link>
                                                        )}
                                                        key={departments1.id}
                                                        collapsed={!openNodes[departments1.id]}
                                                        onClick={() => handleToggle(departments1.id)}
                                                    >
                                                        {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                            <Link to={`/department/${departments2.id}`} className='depsUser' key={departments2.id}>
                                                                <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                            </Link>
                                                        ))}
                                                    </TreeView>
                                                ))}
                                            </TreeView>
                                        ))}
                                    </TreeView>
                                );
                            })}
                        </div>
                    </div>
                ) : ''}
            </div>
        </div>
    );
};

export default Sidebar;
