import Topbar from "../Topbar/Topbar";
import React, {useEffect, useRef, useState} from "react";
import {Link} from "react-router-dom";
import './VacanciesAdd.css'
import axios from "axios";

function VacanciesAdd() {
    const [deps, setDeps] = useState([])
    const [user, setUser] = useState([])
    const departmentRef = useRef()


    useEffect(() => {
        const getDeps = async () => {
            const deps = await axios.get('/api/departments/')
            setDeps(deps.data)
            const user = await axios.get('/api/user/')
            setUser(user.data)

        }

        getDeps()
    }, [])


    const clickHandler = () => {
        const selectedId = departmentRef.current.value; // Получаем значение выбранного элемента
        console.log('Выбранный id:', selectedId);
    };

    return (
        <>
            <Topbar/>
            <div className='VacationsAdd'>
                <div className="VacationsContainerAdd">

                    <br/>
                    <div className="VacationsMainAdd">
                        <h2 style={{textAlign:'center'}}>Добавить вакансию</h2>
                        <div>

                            <select ref={departmentRef} className='SelectVacation' onClick={clickHandler}>
                                <option value='' >Команда</option>
                                {deps.map((dep, name) => <option key={name}
                                                                 value={dep.id} >{dep.department_name}</option>)}
                            </select>
                        </div>
{/*                        <div>
                        <select className='SelectVacation'>
                            <option value=''>Статус</option>
                            {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
                        </select>
                        </div>
                        <div>
                            <input type='text' placeholder='Код вакансии' className='InputVacation'/>
                        </div>
                        <div>
                            <select className='SelectVacation'>
                                <option value=''>Компания</option>
                                {deps.map((dep, name) => <option key={name} value={dep.id}>{dep.department_name}</option>)}
                            </select>
                        </div>
                        <div>
                            <select className='SelectVacation'>
                                <option value=''>Сотрудник</option>
                                {user.map((user, name) => <option key={name} value={user.id}>{user.user_name}</option>)}
                            </select>
                        </div>
                        <div>
                            <input type='text' placeholder='Код проекта' className='InputVacation'/>
                        </div>
                        <div>
                            <input type='text' placeholder='Грейд' className='InputVacation'/>
                        </div>*/}
                        <div style={{marginTop:'20px'}}>
                        <label style={{paddingRight:'10px'}}>Нужна вакансия после завершения проекта?</label>
                        <label htmlFor='Vacation'>
                            <input
                            type='radio'
                            value={true}
                            id='Vacation'
                            name='Vacation'

                        />Да</label>
                        <label htmlFor='Vacation1'>
                            <input
                            type='radio'
                            value={false}
                            id='Vacation'
                            name='Vacation'
                        />Нет</label>
                        </div>
                        <div style={{textAlign:'center',paddingTop:"20px"}}>
                            <button className="filterButton">Сохранить</button>
                            <button className="filterButton">Сбросить</button>
                        </div>
                    </div>
                </div>
            </div>

        </>

    )
}
export default VacanciesAdd
