import Topbar from "../Topbar/Topbar";
import React, {useEffect, useRef, useState} from "react";
import {Link} from "react-router-dom";
import './VacanciesAdd.css'
import axios from "axios";

function VacanciesAdd() {
    const [deps, setDeps] = useState([])
    const department = useRef()
    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/departments/')
            setDeps(res.data)
        }

        getDeps()
    }, [])
    return (
        <>
            <Topbar/>
            <div className='Vacations'>
                <div className="VacationsContainer">
                    <h2>Вакансии</h2>
                    <br/>
                    <div className="VacationsMain">
                        <div>
                            <input
                                type='search'
                                placeholder="Наименование вакансии"
                                className="ModalInput"
                                style={{'marginRight': '10px'}}
                            />
                        </div>
                        <div>
                            <select ref={department} className='deps'>
                                <option value=''>Выберите подразделение</option>
                                {deps.map((dep, name) => <option key={name}
                                                                 value={dep.id}>{dep.department_name}</option>)}
                            </select>
                        </div>
                        <div>
                            <button className="filterButton">Найти</button>
                            <button className="filterButton">Сбросить</button>
                        </div>
                    </div>
                    <div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default VacanciesAdd
