import { Link } from 'react-router-dom'
import { Delete } from '@material-ui/icons'
import axios from 'axios'

import './Employee.css'
import { useEffect, useState } from 'react'

export default function Employee({user}) {
  const localUser = JSON.parse(localStorage.getItem('user'))

  const usersStatuses = {0: '', 1: 'start', 2: 'end', 3: 'vacation', 4: 'deleted'}
  
  function findStatus(k){
    for(let i in usersStatuses){
      if(i == k){
        return usersStatuses[i]
      }
    }
  }

  const [date, setDate] = useState(null)
  const [time, setTime] = useState(null)

  // Проверка, если пользователь закончил работу, то вывод даты, когда он закончил
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/user/')
      res.data.sort((p1, p2) => {
        return new Date(p2.workStart) - new Date(p1.workStart)
      })
      let data = (res.data[0].workEnd)
      data = new Date(data)
      data = data.toLocaleString()
      data = data.split(',')
      let dataF = data[0].split('.')
      let dataS = data[1].split(':')
      dataF = dataF[0]+'/'+dataF[1]
      dataS = dataS[0]+':'+dataS[1]
      setDate(dataF)
      setTime(dataS)
    }
    if(user.status === 2){
      fetchUser()
    }
  }, [user._id])

  const [dateStart, setDateStart] = useState(null)
  const [dateEnd, setDateEnd] = useState(null)

  // Проверка, если пользователь в отпуске или на больничном, то вывод даты начала и конца
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/user'+localUser._id)
      res.data.sort((p1, p2) => {
        return new Date(p2.start) - new Date(p1.start)
      })
      let dataStart = (res.data[0].start)
      let dataEnd = (res.data[0].end)
      dataStart = new Date(dataStart)
      dataStart = dataStart.toLocaleString()
      dataStart = dataStart.split(',')
      dataEnd = new Date(dataEnd)
      dataEnd = dataEnd.toLocaleString()
      dataEnd = dataEnd.split(',')
      setDateStart(dataStart[0])
      setDateEnd(dataEnd[0])
    }
    if(user.status === 3){
      fetchUser()
    }
  }, [user._id])

  return (<>
    {user.status === 4 && !localUser.isAdmin
      ? <div></div> 
      : <div className={'employee ' + (findStatus(user.status))}>
          <div className='top'>
            {localUser.isAdmin
              ? <Link to={`/profile/${user.id}`} className='name'><span>{user.user_name}</span></Link>
              : <div className='name'><span>{user.user_name}</span></div>
            }
          </div>
          <div className='down'>
              <div className='downRight'>
                <div className='emailEmployee'>{user.email.toLowerCase()}</div>
                {user.status === 2 && <div>{date} {time}</div>}
                {user.status === 3 && <div>{dateStart} - {dateEnd}</div>}
                </div>
          </div>
        </div>
    }</>
  )
}
