import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = () => {
    const [dep, setDep] = useState([]);

    useEffect(() => {
        const getDeps = async () => {
            try {
                const res = await axios.get('/api/department_tree_to_json/');
                if (res.data && res.data.length > 0) {
                    setDep(res.data);
                    // Найти нужный ID и автоматически раскрыть все вложенности до этого ID
                    const idToOpen = '21'; // Замените на ваш ID, который нужно открыть
                    const openTreeToId = (nodes) => {
                        for (let node of nodes) {
                            if (node.department_tree_to_json.id === idToOpen) {
                                return true; // Найдено нужное ID, прерываемся
                            }
                            if (node.department_tree_to_json.Subclasses) {
                                if (openTreeToId(node.department_tree_to_json.Subclasses)) {
                                    node.defaultCollapsed = false; // Раскрываем узел, если найдено ID
                                    return true;
                                }
                            }
                        }
                        return false;
                    };
                    openTreeToId(res.data);
                    setDep([...res.data]); // Обновляем состояние с открытыми узлами
                } else {
                    setDep([]);
                }
            } catch (error) {
                console.error('Ошибка при загрузке данных:', error);
                setDep([]); // Обработка ошибки, если не удалось загрузить данные
            }
        };
        getDeps();
    }, []);

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                {dep.length >= 1 ? (
                    <div className="tree">
                        <div className="treeWrapper">
                            {dep.map((node, i) => (
                                <TreeView
                                    key={node.department_tree_to_json.id + '|' + i}
                                    nodeLabel={(
                                        <Link to={`/department/${node.department_tree_to_json.id}`} className='level1'>
                                            <span className="node">{node.department_tree_to_json.Name}</span>
                                        </Link>
                                    )}
                                    defaultCollapsed={node.defaultCollapsed}
                                >
                                    {node.department_tree_to_json.Subclasses && node.department_tree_to_json.Subclasses.map((departments) => (
                                        <TreeView
                                            key={departments.id}
                                            nodeLabel={(
                                                <Link to={`/department/${departments.id}`} className='depsUser'>
                                                    <span className="node">{departments.Name}</span>
                                                </Link>
                                            )}
                                            defaultCollapsed={true}
                                        >
                                            {!!departments.Subclasses && departments.Subclasses.map((departments1) => (
                                                <TreeView
                                                    key={departments1.id}
                                                    nodeLabel={(
                                                        <Link to={`/department/${departments1.id}`} className='depsUser'>
                                                            <span className="node">{departments1.Name}</span>
                                                        </Link>
                                                    )}
                                                    defaultCollapsed={true}
                                                >
                                                    {!!departments1.Subclasses && departments1.Subclasses.map((departments2) => (
                                                        <Link to={`/department/${departments2.id}`} className='depsUser' key={departments2.id}>
                                                            <div style={{ paddingBottom: '8px' }}>{departments2.Name}</div>
                                                        </Link>
                                                    ))}
                                                </TreeView>
                                            ))}
                                        </TreeView>
                                    ))}
                                </TreeView>
                            ))}
                        </div>
                    </div>
                ) : ''}
            </div>
        </div>
    );
};

export default Sidebar;
