import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './Sidebar.css';
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css';

const Sidebar = ({ initialOpenDepartmentId }) => {
    const [dep, setDep] = useState([]);

    useEffect(() => {
        const getDeps = async () => {
            const res = await axios.get('/api/department_tree_to_json/');
            setDep(res.data);
        };
        getDeps();
    }, []);

    const findAndOpenDepartment = (departments, id) => {
        for (const department of departments) {
            if (department.id === id) {
                department.isOpen = true;
                return true;
            } else if (department.Subclasses) {
                const found = findAndOpenDepartment(department.Subclasses, id);
                if (found) {
                    department.isOpen = true;
                    return true;
                }
            }
        }
        return false;
    };

    useEffect(() => {
        if (initialOpenDepartmentId) {
            const updatedDep = [...dep];
            for (const node of updatedDep) {
                const found = findAndOpenDepartment(node.department_tree_to_json.Subclasses, initialOpenDepartmentId);
                if (found) {
                    setDep(updatedDep);
                    break;
                }
            }
        }
    }, [initialOpenDepartmentId]);

    const renderTree = (node) => {
        const label = (
            <Link to={`/department/${node.department_tree_to_json.id}`} className="level1">
                <span className="node">{node.department_tree_to_json.Name}</span>
            </Link>
        );

        return (
            <TreeView
                key={node.department_tree_to_json.id}
                nodeLabel={label}
                defaultCollapsed={!node.isOpen}
            >
                {node.department_tree_to_json.Subclasses.map((subclass) => {
                    return (
                        <TreeView
                            key={subclass.id}
                            nodeLabel={
                                <Link to={`/department/${subclass.id}`} className="depsUser">
                                    <span className="node">{subclass.Name}</span>
                                </Link>
                            }
                            defaultCollapsed={!subclass.isOpen}
                        >
                            {subclass.Subclasses && subclass.Subclasses.map((subSubclass) => {
                                return (
                                    <Link to={`/department/${subSubclass.id}`} className="depsUser" key={subSubclass.id}>
                                        <div style={{ paddingBottom: '8px' }}>{subSubclass.Name}</div>
                                    </Link>
                                );
                            })}
                        </TreeView>
                    );
                })}
            </TreeView>
        );
    };

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <div className="tree">
                    <div className="treeWrapper">
                        {dep.map((node) => {
                            return renderTree(node);
                        })}
                    </div>
                </div>
            </div>
            <div></div>
        </div>
    );
};

export default Sidebar;
