import { Link } from 'react-router-dom'
import { Delete } from '@material-ui/icons'
import axios from 'axios'

import './Employee.css'
import { useEffect, useState } from 'react'


export default function Employee({user}) {

  const setDat = new Date()
  setDat.setDate(setDat.getDate())
  const dat=setDat.toLocaleDateString()

  const usersStatuses = {0: '', 1: 'start', 2: 'end', 3: 'sick_leave', 4: 'deleted' ,5: 'vacation', 6: 'time_off', 7: 'other'}
  function findStatus(k){
    for(let i in usersStatuses){
      if(i == k){
        return usersStatuses[i]
      }
    }
  }

  const [admin, setAdmin] = useState([''])
  const [posts, setPosts] = useState([])
  const [isFetching, setIsFetching] = useState(false)
  const [date, setDate] = useState(null)
  const [time, setTime] = useState(null)
  const [dateStart, setDateStart] = useState(null)
  const [dateEnd, setDateEnd] = useState(null)


  const localUser = JSON.parse(localStorage.getItem('user'))


  const fetchUserpost = async () => {
    const res = await axios.get('/api/post?type=1&id='+user.id)
    const users = await axios.get('/api/user/'+user.id)
    console.log("REEEEEEEEEEEE=",users.data.isadmin)

    if(users.data.isadmin===true){
      setAdmin('*')}else{
        setAdmin('')
      }

    setDate('')
    setTime('')
try{
  setDate(res.data[0].workdate)
  setTime(res.data[0].workend)
}
catch {
}
    setPosts(res.data.sort((p1, p2) => {
      return new Date(p2.workstart) - new Date(p1.workstart)
      }))
    setIsFetching(false)

  }







/*  // Проверка, если пользователь закончил работу, то вывод даты, когда он закончил
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/post/')
      res.data.sort((p1, p2) => {
        return new Date(p2.workStart) - new Date(p1.workStart)
      })
      let data = (res.data[0].workEnd)
      data = new Date(data)
      data = data.toLocaleString()
      data = data.split(',')
      let dataF = data[0].split('.')
      let dataS = data[1].split(':')
      dataF = dataF[0]+'/'+dataF[1]
      dataS = dataS[0]+':'+dataS[1]
      setDate(dataF)
      setTime(dataS)
    }
    if(user.status === 2){
      fetchUser()
    }
  }, [user._id])*/



  // Проверка, если пользователь в отпуске или на больничном, то вывод даты начала и конца
  useEffect(() => {
    const fetchUser = async () => {
      const res = await axios.get('/api/vacation?id=' + user.id)

      if (res.data.length === 0) {
        setDateStart(global.workdate = '')
        setDateEnd(global.worked = '')
      } else {
        setDateStart(res.data[0].workdate)
        setDateEnd(res.data[0].worked)

      }
      const users = await axios.get('/api/user/'+user.id)
      console.log("REEEEEEEEEEEE=",users.data.isadmin)

      if(users.data.isadmin===true){
        setAdmin('*')}else{
        setAdmin('')
      }
    }


    if (user.status === 3) {
        fetchUser()
      }

      if (user.status === 7) {
        fetchUser()
      }
      if (user.status === 6) {
        fetchUser()
      }

      if (user.status === 5) {
        fetchUser()
      }
      if (user.status === 2) {
        fetchUserpost()
      }
  }, [user._id])



  return (<>

        {user.status === 4 && !localUser.isAdmin
            ?
            <div></div>
            :
            <div className={'employee ' + (findStatus(user.status))}>
              <div className='top'>{localUser.isAdmin
                  ?
                  <Link to={`/profile/${user.id}`} className='name'><span className='name'>{user.user_name}{admin}</span></Link>
                  : <div className='name'><span className='name'>{user.user_name}{admin}</span></div>
              }
              </div>
              <div className='down'>
                <div className='downRight'>
                  {/*<div className='emailEmployee'>{user.email}</div>*/}
                  {user.status === 5 && <div className='vacationText'>Отпуск:{dateStart}-{dateEnd}</div>}
                  {user.status === 6 && <div className='vacationText'>Отгул:{dateStart}-{dateEnd}</div>}
                  {user.status === 7 && <div className='vacationText'>Другая причина:{dateStart}-{dateEnd}</div>}
                  {user.status === 3 && <div className='vacationText' >Больничный:{dateStart}-{dateEnd}</div>}
                  {user.status === 2 && <div className='vacationText' >{date}  {time}</div>}
                </div>
              </div>
            </div>
        }</>
  )
}


