import "./VcanciesItem.css"
import moment from "moment";
import React, {useRef, useState} from "react";
import ModalVacancies from "../Templates/ModalVacancies/ModalVacancies";
import axios from "axios";

const VacanciesItem =({department,vacancies})=> {
    const localUser = JSON.parse(localStorage.getItem('user'))


    const [vacanciesId, setVacanciesId] = useState("")
    const [modalVacations, setModalVacations] = useState(false)
    const [inputValue, setInputValue] = useState("")
    const [inputValueDescription, setInputValueDescription] = useState("")
    const [inputValueStatuses, setInputValueStatuses] = useState("")
    const [inputValueGrade, setInputValueGrade] = useState("")


    const [selectedVacanciesName, setSelectedVacanciesName] = useState('');
    const [selectedVacanciesStatuses, setSelectedVacanciesStatuses] = useState('');
    const [selectedVacanciesDescription, setSelectedVacanciesDescription] = useState('');
    const [selectedVacanciesGrade, setSelectedVacanciesGrade] = useState('');

    const [isOpen, setIsOpen] = useState(true)
    const [find, setFind] = useState([])


    const statusRef = useRef()

    const searchRef = useRef()




    const handleClick = (name, grade,id,description,statuses) => {
        if(localUser.isAdmin===true) {
            setModalVacations(true);
            setVacanciesId(id)

            setSelectedVacanciesName(name);
            setInputValue(name); // Установка начального значения для name


            setSelectedVacanciesDescription(description)
            setInputValueDescription(description)

            setSelectedVacanciesGrade(grade);
            setInputValueGrade(grade); // Установка начального значения для grade

            setSelectedVacanciesStatuses(statuses)
            setInputValueStatuses(statuses)

        }

    };

    const handleNameInputChange = (e) => {
        setInputValue(e.target.value);
    }

    // Обработчик изменения значения для поля ввода с grade
    const handleDescriptionInputChange = (e) => {
        setInputValueDescription(e.target.value);
    }

    const handleGradeInputChange = (e) => {
        setInputValueGrade(e.target.value);
    }
    const handleStatusesInputChange = (e) => {
        setInputValueStatuses(e.target.value);
    }

    const reset = () => {
        searchRef.current.value = ''
        setSearchValue('')
        setStartDate('')
        setEndDate('')
    }


    const handleStartDateChange = (e) => {
        setStartDate(e.target.value)
    };

    const handleEndDateChange = (e) => {
        setEndDate(e.target.value)
    }

    const selectValue = () => {
        setInputValueStatuses(statusRef.current.value)
    }


    const handleCombinedFilter = () => {
        if (searchValue.length === 0 && !startDate && !endDate) {
            // Если нет поискового запроса и не выбраны даты, просто отобразите сообщение об ошибке или что-то подобное
            setFilteredData([]);
            setFind([]);
            setIsOpen(true)
            return
        }

        if (searchValue.length !== 0) {
            // Если есть поисковой запрос, выполняем фильтрацию по нему
            setFind(vacancies.filter(vac => {
                return vac.name.toLowerCase().includes(searchValue.toLowerCase());
            }));
            setFilteredData([]);
            setIsOpen(false);
        } else {
            // Если нет поискового запроса, выполняем фильтрацию по дате
            setFilteredData(vacancies.filter(item => {
                const itemDate = new Date(item.create_date);
                const start = startDate ? new Date(startDate) : null;
                const end = endDate ? new Date(endDate) : null;
                return (!start || itemDate >= start) && (!end || itemDate <= end);
            }));
            setFind([]);
            setIsOpen(false);
        }
    }


    const ChangeData = async () => {

        const ChangeData ={
            namevacanciesId:vacanciesId,
            namevacancies:inputValue,
            description:inputValueDescription,
            Statuses:inputValueStatuses,
            grade:inputValueGrade,
            statuses:statusRef.current.value
        }
        const res = await axios.put('/api/Vacations/', ChangeData)
    }


    const date = moment().format('YYYY-MM-DD');

    return (
        <>
            <div>
                <div>{department}</div>
                {vacancies.map(vac => (
                    <div className='VacationsContainer' onClick={() => handleClick(vac.name, vac.grade,vac.id,vac.description,vac.status_id)}>
                        <div id="item-01">{vac.name}</div>
                        <div id="item-11">{date === vac.create_date ? 'NEW' : ''}</div>
                        <div id="item-21">{vac.description}</div>
                        <div id="item-31"></div>
                        <div id="item-41">{moment(vac.create_date).format('DD.MM.YYYY')}</div>
                    </div>
                ))}
                <ModalVacancies active={modalVacations} setActive={setModalVacations}>
                    <hr/>
                    <div>
                        <input type='text' className="ModalInputVacancies"
                               value={inputValue}
                               onChange={handleNameInputChange}
                        />
                    </div>
                    <div>
                    <textarea  className='textDescription'
                               value={inputValueDescription}
                               onChange={handleDescriptionInputChange}
                    />
                    </div>
                    <div>
                        Статус
                    </div>
                    <div>
                        <select ref={statusRef} value={inputValueStatuses} onChange={selectValue}>
                            {status.map((status, name) => <option key={name} value={status.id}>{status.name}</option>)}
                        </select>
                    </div>


                    <div>
                        <div>
                            <label>Грейд</label>
                        </div>
                        <input type='number'
                               value={inputValueGrade}
                               onChange={handleGradeInputChange}
                        />
                    </div>
                    <br/>
                    <div className='divHistory'>
                        История
                    </div>
                    <br/>
                    <div>
                        <button className="ModalButton" type="submit" onClick={ChangeData}>
                            Сохранить
                        </button>
                    </div>
                </ModalVacancies>
            </div>
        </>
    )
}
export default VacanciesItem
