import Topbar from "../../components/Topbar/Topbar";

import React, {useMemo, useRef, useState} from "react";
import {useParams} from "react-router-dom";
import axios from "axios";
import RepostsInfo from "../../components/ReportsInfo/RepostsInfo";
import Pagination from "../../components/Pagination/Pagination";

export default function ReportsHistory() {
    const [sortConfig, setSortConfig] = useState({key: null, direction: 'ascending'});

    const [currentPage, setCurrentPage] = useState(1)
    const [postsPerPage] = useState(30)

    const lastPostIndex = currentPage * postsPerPage
    const firstPostIndex = lastPostIndex - postsPerPage


    const [error, setError] = useState('')
    const historyStartWork = useRef(null)
    const historyEndWork = useRef(null)
    const [isFilter, setIsFilter] = useState(false)
    const [posts, setPosts] = useState([])
    const currentlyPosts = posts.slice(firstPostIndex, lastPostIndex)
    const username = useParams()

    const paginate = pageNumber => setCurrentPage(pageNumber)


    const handleClick = async () => {
        const start = historyStartWork.current.value
        const end = historyEndWork.current.value
        if (start === "" || end === "") {
            setError('Введите дату')
        } else {
            if (start !== '' || end !== '') {
                setError('Выберите дату')
            }
            if (end < start) {
                setError('Конечная дата меньше начальной')
            } else {

                const filter = {
                    startdate: historyStartWork.current.value,
                    enddate: historyEndWork.current.value,
                }
                setError('')

                const fill = async () => {
                    const res = await axios.post('/api/post/Reports', filter)
                    setPosts(res.data)
                    setError('')
                }
                fill()
            }
        }
    }
    const handleClickCancel = async () => {
        setError('')
        setIsFilter(false)
        historyStartWork.current.value = ''
        historyEndWork.current.value = ''
    }


    const requestSort = (key) => {
            let direction = 'ascending';
            if (sortConfig.key === key && sortConfig.direction === 'ascending') {
                direction = 'descending';
            }
            setSortConfig({ key, direction });
    };

    const sortedPosts = useMemo(() => {
        const sortablePosts = [...posts];
        if (sortConfig !== null) {
            sortablePosts.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortablePosts;
    }, [posts, sortConfig]);

    return (
        <>
            <Topbar/>
            <div className='Reports'>
                <div className="ReportsContainer">
                    <h2 className='ReportsMainText'>Отчет по истории</h2>

                    <hr/>
                    {error && <div className='errorHistoryInfo'>{error}</div>}
                    <br/>
                    <input
                        type='date'
                        placeholder="Начало работы"
                        className="ModalInput"
                        ref={historyStartWork}
                        style={{'marginRight': '10px'}}
                    />
                    <input
                        type='date'
                        placeholder="Конец работы"
                        className="ModalInput"
                        ref={historyEndWork}
                        style={{'marginRight': '10px'}}
                    />
                    <button className="filterButton" onClick={handleClick}>ок</button>
                    <button className="filterButton" onClick={handleClickCancel}>сбросить</button>
                    <br/>
                    <br/>
                    <hr/>
                    <br/>
                    <table>
                        <thead>
                        <tr>
                            <td className="historyinfoNumber" onClick={() => requestSort('name', setSortConfigName)}>
                                № {sortConfigName.key === 'name' && (
                                <span>{sortConfigName.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </td>
                            <td className="historyinfoNumber" onClick={() => requestSort('FIO', setSortConfigFIO)}>
                                ФИО {sortConfigFIO.key === 'FIO' && (
                                <span>{sortConfigFIO.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </td>
                            <td className="historyinfoNumber" onClick={() => requestSort('start', setSortConfigStart)}>
                                Начало {sortConfigStart.key === 'start' && (
                                <span>{sortConfigStart.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </td>
                            <td className="historyinfoNumber" onClick={() => requestSort('date', setSortConfigDate)}>
                                Конец {sortConfigDate.key === 'date' && (
                                <span>{sortConfigDate.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </td>
                            <td className="historyinfoNumber" onClick={() => requestSort('long', setSortConfigLong)}>
                                Длительность {sortConfigLong.key === 'long' && (
                                <span>{sortConfigLong.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </td>
                            <td className="historyinfoNumber" onClick={() => requestSort('comment', setSortConfigComment)}>
                                Комментарий {sortConfigComment.key === 'comment' && (
                                <span>{sortConfigComment.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </td>
                        </tr>
                        </thead>
                        <tbody>
                        {sortedPosts.map((posts, id) => (
                            <RepostsInfo key={id} posts={posts} username={username} />
                        ))}
                        </tbody>
                    </table>
                    {
                        sortedPosts.map((posts, id) => <RepostsInfo key={id} posts={posts} username={username}/>)
                    }

                    <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate}
                                currentPage={currentPage}/>

                </div>
            </div>
        </>
    )
}
