import React, { useEffect, useState } from 'react';
import axios from "axios";

function Role() {
    const [roles, setRoles] = useState([]);
    const [users, setUsers] = useState([]);
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [searchTermUsers, setSearchTermUsers] = useState('');
    const [userId, setUserId] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const resRoles = await axios.get('/api/roles/');
                const rolesData = resRoles.data[0].get_roles;
                setRoles(rolesData);

                const resUsers = await axios.get('/api/user/');
                setUsers(resUsers.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    const handleInputChangeUsers = (e) => {
        const termCities = e.target.value;

        if(termCities.length===0){
            setUserId('')
        }
        setSearchTermUsers(termCities);

        if (termCities === '') {
            setFilteredUsers([]);
            return;
        }

        const filtered = users.filter(user =>
            user.user_name && user.user_name.toLowerCase().includes(termCities.toLowerCase())
        );
        setFilteredUsers(filtered);
    };

    const handleUserClickCities = (user_name, id) => {
            setSearchTermUsers(user_name)
            setFilteredUsers([])
            setUserId(id)
    }

    const addRole = () => {
    setUserId('')
    setSearchTermUsers('')
    }


    return (
        <div className="ReportsButton">
            <div style={{ display: 'flex', gap: '200px' }}>
                <div>
                    <label>Имя сотрудника</label>
                    <input className="ModalInputUpdate" type="text"
                           value={searchTermUsers}
                           onChange={handleInputChangeUsers} />
                    {filteredUsers.length > 0 && (
                        <div className='divSelectRegister'>
                            {filteredUsers.map((user, id) => (
                                <div className='selectNameDiv' key={id} onClick={() => handleUserClickCities(user.user_name, user.id)}>
                                    {user.user_name}
                                </div>
                            ))}
                        </div>
                    )}
                </div>
                <div>
                    <label>Роль</label>
                    <select className="ModalInputUpdate">
                        {roles.map((role, index) => (
                            <option key={index} value={role.id}>
                                {role.name}
                            </option>
                        ))}
                    </select>
                </div>
            </div>
            <div>
                {userId ?
                    <button onClick={addRole}>
                        Добавить
                    </button>
                    :''
                }

            </div>
            <table className="skills-table">
                <thead>
                <tr className="skills-header">
                    <th>ФИО</th>
                    <th>Роль</th>
                    <th>Оценка руководителя</th>
                    <th></th>

                </tr>
                </thead>
                <tbody>
                {userSkills.map((array, id) => (
                    <tr className="skill-item" key={id}>
                        <td>
                            {array.name}
                        </td>
                        <td>

                            <select
                                name='gradeSelf'
                                defaultValue={array.self_grade}
                                onChange={(event) => changeGrade(event, array.id)}
                            >
                                {[1, 2, 3, 4, 5].map(number => (
                                    <option key={number} value={number}>{number}</option>
                                ))}
                            </select>
                        </td>
                        <td>
                            <select
                                name='gradeHead'
                                defaultValue={array.head_grade}
                                disabled={!localUser.isAdmin}
                                onChange={(event) => changeGrade(event, array.id)}
                            >
                                {array.head_grade === null && !localUser.isAdmin ? <option>Нет оценки</option> : ''}
                                {array.head_grade === null ? <option>Нет оценки</option> : ''}
                                {[1, 2, 3, 4, 5].map(number => (
                                    <option key={number} value={number}>{number}</option>
                                ))}
                            </select>
                        </td>
                        <td><button value={array.id} className='SkillButton' onClick={SkillDelete}>Удалить</button></td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default Role;
