import React, { useRef, useState, useMemo } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import RepostInfoNominations from "../../components/RepostInfoNominations/RepostInfoNominations";
import Topbar from '../../components/Topbar/Topbar'
import './ReportsNominations.css'

export default function ReportsNominations() {
    const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
    const [error, setError] = useState('')
    const historyStartWork = useRef(null)
    const historyEndWork = useRef(null)
    const [posts, setPosts] = useState([])
    const username = useParams()

    const handleClick = async () => {
        const start = historyStartWork.current.value
        const end = historyEndWork.current.value
        if (start === "" || end === "") {
            setError('Введите дату')
        } else {
            if (start !== '' || end !== '') {
                setError('Выберите дату')
            }
            if (end < start) {
                setError('Конечная дата меньше начальной')
            } else {
                const filter = {
                    startDateNomination: historyStartWork.current.value,
                    endDateNomination: historyEndWork.current.value,
                }
                setError('')
                const fill = async () => {
                    const res = await axios.post('/api/post/Reports', filter)
                    setPosts(res.data)
                    setError('')
                }
                fill()
            }
        }
    }

    const handleClickCancel = async () => {
        setError('')
        historyStartWork.current.value = ''
        historyEndWork.current.value = ''
    }

    const requestSort = (key) => {
        let direction = 'ascending';
        if (sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    const sortedPosts = useMemo(() => {
        const sortablePosts = [...posts];
        if (sortConfig.key !== null) {
            sortablePosts.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortablePosts;
    }, [posts, sortConfig]);

    return (
        <>
            <Topbar />
            <div className='Reports'>
                <div className="ReportsContainer">
                    <h2 className='ReportsMainText'>Отчет по номинациям</h2>
                    <hr />
                    {error && <div className='errorHistoryInfo'>{error}</div>}
                    <br />
                    <input
                        type='date'
                        placeholder="Начало работы"
                        className="ModalInput"
                        ref={historyStartWork}
                        style={{ 'marginRight': '10px' }}
                    />
                    <input
                        type='date'
                        placeholder="Конец работы"
                        className="ModalInput"
                        ref={historyEndWork}
                        style={{ 'marginRight': '10px' }}
                    />
                    <button className="filterButton" onClick={handleClick}>ок</button>
                    <button className="filterButton" onClick={handleClickCancel}>сбросить</button>
                    <br />
                    <br />
                    <hr />
                    <br />
                    <table>
                        <thead>
                        <tr>
                            <th className="historyinfoNumber" onClick={() => requestSort('name')}>
                                Кто голосовал {sortConfig.key === 'name' && (
                                <span>{sortConfig.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </th>
                            <th className="historyinfoNumber" onClick={() => requestSort('FIO')}>
                                Номинировал {sortConfig.key === 'FIO' && (
                                <span>{sortConfig.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </th>
                            <th className="historyinfoNumber" onClick={() => requestSort('start')}>
                                Имя номинации {sortConfig.key === 'start' && (
                                <span>{sortConfig.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </th>
                            <th className="historyinfoNumber" onClick={() => requestSort('date')}>
                                Дата голосования {sortConfig.key === 'date' && (
                                <span>{sortConfig.direction === 'ascending' ? '▼' : '▲'}</span>
                            )}
                            </th>
                        </tr>
                        </thead>
                        <tbody>
                        {sortedPosts.map((post, id) => <RepostInfoNominations key={id} post={post} username={username} />)}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    )
}
