import React, { useEffect, useState } from 'react';
import axios from "axios";



function Role() {
    const [roles, setRoles]=useState([])
    const [users, setUsers]=useState([])
    const [filteredUsers, setFilteredUsers] = useState([]);
    const [searchTermCities, setSearchTermCities] = useState('');

    useEffect(() => {
        const rolesName = async () => {
            const res = await axios.get('/api/roles/')
            const roles=res.data[0].get_roles
            setRoles(roles)
            const users= await axios.get('/api/user/')
            console.log('users=',users.data)
            setUsers(users.data)
        }
        rolesName()
    }, [])

    const handleInputChangeUsers = (e) => {
        const termCities = e.target.value;
        setSearchTermCities(termCities);

        if (termCities === '') {

            setFilteredUsers([]);

            return;
        }

        const filtered = users.filter(user =>
            user.user_name && user.user_name.toLowerCase().includes(termCities.toLowerCase())
        );
        setFilteredUsers(filtered);
    }

    return (
        <div className="ReportsButton">
            <div style={{display:'flex', gap:'200px'}}>
            <div>
                <label>Имя сотрудника</label>
                    <input className="ModalInputUpdate" type="text"
                           onChange={handleInputChangeUsers}
                    />
            </div>
                <div>
                    {filteredUsers.length > 0 && (
                        <div className='divSelectRegister'>
                            {filteredUsers.map((city, id) => (
                                <div className='selectNameDiv' key={id} value={city.id}>
                                    {users.user_name}
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            <div>
                <label>Роль</label>
                <select className="ModalInputUpdate">
                    {roles.map((role, index) => (
                        <option
                            key={index}
                            value={role.id}
                        >
                            {role.name}
                        </option>
                    ))}
                </select>
            </div>
            </div>
        </div>
    );

}

export default Role;
