import React from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'
import {useEffect, useState} from 'react'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'


const Sidebar = () => {
    const [dep, setDep] = useState([]);

    useEffect(() => {
        const getDeps = async () => {
            try {
                const res = await axios.get('/api/department_tree_to_json/');
                if (res.data && res.data.length > 0) {
                    setDep(res.data);
                } else {
                    setDep([]);
                }
            } catch (error) {
                console.error('Error fetching departments:', error);
                setDep([]);
            }
        };
        getDeps();
    }, []);

    return (
        <div className='sidebar'>
            <div className="sidebarWrapper">
                <Accordion>
                    {dep.map((node, i) => (
                        <AccordionItem key={node.department_tree_to_json.id} title={node.department_tree_to_json.Name}>
                            {node.department_tree_to_json.Subclasses.map((department) => (
                                <AccordionItem key={department.id} title={department.Name}>
                                    {!!department.Subclasses && department.Subclasses.map((subdepartment) => (
                                        <Link to={`/department/${subdepartment.id}`} className='depsUser' key={subdepartment.id}>
                                            <div style={{ paddingBottom: '8px' }}>{subdepartment.Name}</div>
                                        </Link>
                                    ))}
                                </AccordionItem>
                            ))}
                        </AccordionItem>
                    ))}
                </Accordion>
            </div>
        </div>
    );
};

export default Sidebar;
