import React, { useRef, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ModalVacancies from '../../Templates/ModalVacancies/ModalVacancies';
import axios from 'axios';

const ModalVacanciesItem = ({
                            modalVacations,
                            setModalVacations,
                            vacanciesId,
                            setVacanciesId,
                            deps,
                            status,
                            company,
                            users,
                            ChangeData,
                            localUser,
                            selectedVacanciesName,
                            setSelectedVacanciesName,
                            selectedVacanciesDescription,
                            setSelectedVacanciesDescription,
                            selectedVacanciesGrade,
                            setSelectedVacanciesGrade,
                            selectedVacanciesStatuses,
                            setSelectedVacanciesStatuses,
                            selectedDep,
                            setSelectDep,
                            selectCompany,
                            setSelectCompany,
                            selectItc,
                            setSelectItc,
                            projectItem,
                            setProjectItem,
                            plannedReleaseDate,
                            setPlannedReleaseDate,
                            old_user,
                            setOld_user,
                            userId,
                            setUserId,
                            user_name,
                            setUser_name,
                            searchTerm,
                            setSearchTerm,
                            filteredUsers,
                            setFilteredUsers,
                            message,
                            setMessage
                        }) => {

    console.log('change=',ChangeData)
    const statusRef = useRef();
    const departRef = useRef();
    const companyRef = useRef();
    const ITCRef = useRef();
    const projectRef = useRef();
    const DateReleaseRef = useRef();

    const handleNameInputChange = (e) => {
        setSelectedVacanciesName(e.target.value);
    }

    const handleDescriptionInputChange = (e) => {
        setSelectedVacanciesDescription(e.target.value);
    }

    const handleGradeInputChange = (e) => {
        if (/^\d*$/.test(e.target.value)) {
            setSelectedVacanciesGrade(e.target.value);
        }
    };

    const selectValue = () => {
        setSelectedVacanciesStatuses(statusRef.current.value);
    }

    const selectDeps = () => {
        setSelectDep(departRef.current.value);
    }

    const companySelect = () => {
        setSelectCompany(companyRef.current.value);
    }

    const SelectITC = () => {
        setSelectItc(ITCRef.current.value);
    }

    const Project = (e) => {
        if (/^\d*$/.test(e.target.value)) {
            setProjectItem(e.target.value);
        }
    }

    const DataCurrent = () => {
        setPlannedReleaseDate(DateReleaseRef.current.value);
    }

    const handleInputChange = (e) => {
        const term = e.target.value;
        if (term === '') {
            setSearchTerm(null);
        } else {
            setSearchTerm(term);
        }
        const filtered = users.filter(user =>
            user.user_name && user.user_name.toLowerCase().includes(term.toLowerCase())
        );

        if (term === '') {
            setUser_name('');
        } else {
            setFilteredUsers(filtered);
        }
    }

    const handleUserClick = (userName, userId) => {
        setUserId(userId);
        setSearchTerm(userName);
        setFilteredUsers([]);
    }

    return (
        <ModalVacancies active={modalVacations} setActive={setModalVacations}>
            <div className='MessageItemVacancies'>
                <span>{message}</span>
            </div>
            <div>
                <input
                    type='text'
                    className="ModalInputVacancies"
                    value={selectedVacanciesName}
                    onChange={handleNameInputChange}
                />
            </div>
            <div>
        <textarea
            className='textDescription'
            value={selectedVacanciesDescription}
            onChange={handleDescriptionInputChange}
        />
            </div>
            <div>
                <span>Подразделение:</span><br/>
                <select
                    ref={departRef}
                    value={selectedDep}
                    onChange={selectDeps}
                    className='InputVacanciesItemDeps'>
                    {deps.map((dep, name) => (
                        <option key={name} value={dep.id}>{dep.department_name}</option>
                    ))}
                </select>
            </div>

            <div>
                <span>Статус:</span><br/>
                <select
                    ref={statusRef}
                    value={selectedVacanciesStatuses}
                    onChange={selectValue}
                    className='InputVacanciesItemSelect'>
                    {status.map((status, name) => (
                        <option key={name} value={status.id}>{status.name}</option>
                    ))}
                </select>
            </div>

            <div>
                <span>Грейд:</span><br/>
                <input
                    type='text'
                    value={selectedVacanciesGrade}
                    onChange={handleGradeInputChange}
                    placeholder='Грейд'
                    className='InputVacanciesItem'
                />
            </div>
            <div>
                <span>Компания:</span><br/>
                <select
                    ref={companyRef}
                    value={selectCompany}
                    onChange={companySelect}
                    className='InputVacanciesItemSelect'>
                    {company.map((company, name) => (
                        <option key={name} value={company.id}>{company.name}</option>
                    ))}
                </select>
            </div>

            <div className="container">
                <div>
                    <span>Имя сотрудника:</span><br/>
                    <div className='inputDiv'>
                        <input
                            type='text'
                            placeholder='Имя сотрудника'
                            value={searchTerm || user_name}
                            onChange={handleInputChange}
                            className='InputVacanciesItem'
                        />

                        {old_user ? <Link className='ProfileLinkVacancies' style={{ paddingLeft: '10px' }} to={`/profile/${old_user}`}>Перейти в профиль</Link> : ''}

                        {filteredUsers.length > 0 && (
                            <div className="userListContainer">
                                {filteredUsers.map((user, index) => (
                                    <div className='selectNameDiv' key={index} value={user.id} onClick={() => handleUserClick(user.user_name, user.id)}>
                                        {user.user_name}
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                    <div>
                        <span>Дата выхода:</span><br/>
                        <input
                            type='date'
                            ref={DateReleaseRef}
                            placeholder='ИТС'
                            className='InputVacanciesItem'
                            value={plannedReleaseDate || ''}
                            onChange={DataCurrent}
                        />
                    </div>
                    <div>
                        <span>ИТС:</span><br/>
                        <input
                            type='text'
                            ref={ITCRef}
                            value={selectItc}
                            onChange={SelectITC}
                            placeholder='ИТС'
                            className='InputVacanciesItem'
                        />
                    </div>
                    <div>
                        <span>Проект:</span><br/>
                        <input
                            type='text'
                            ref={projectRef}
                            value={projectItem}
                            onChange={Project}
                            placeholder='Проект(Нет проекта=0)'
                            className='InputVacanciesItem'
                        />
                    </div>
                </div>
                <br/>
                <div className='divHistory'>
                    История
                </div>
                <br/>
                <div>
                    <button className="ModalButton" type="submit" onClick={ChangeData}>
                        Сохранить
                    </button>
                </div>
            </div>
        </ModalVacancies>
    );
}

export default ModalVacanciesItem;
