import React, {useRef, useState,useEffect} from 'react'
import Topbar from '../../components/Topbar/Topbar'
import axios from "axios";
import {useParams} from "react-router-dom";
import RepostsInfo from "../../components/ReportsInfo/RepostsInfo";
import './Reports.css'
import Pagination from "../../components/Pagination/Pagination";


export default function Reports() {
    const initialSortConfig = JSON.parse(localStorage.getItem('sortConfig')) || { key: null, direction: 'ascending' };
    const [sortConfig, setSortConfig] = useState(initialSortConfig);

    useEffect(() => {
        localStorage.setItem('sortConfig', JSON.stringify(sortConfig));
    }, [sortConfig]);

    const [currentPage, setCurrentPage] = useState(1)
    const [postsPerPage] = useState(30)

    const lastPostIndex = currentPage * postsPerPage
    const firstPostIndex = lastPostIndex - postsPerPage


    const [error, setError] = useState('')
    const historyStartWork = useRef(null)
    const historyEndWork = useRef(null)
    const [isFilter, setIsFilter] = useState(false)
    const [posts, setPosts] = useState([])
    const currentlyPosts = posts.slice(firstPostIndex, lastPostIndex)
    const username=useParams()

    const paginate = pageNumber => setCurrentPage(pageNumber)


    const handleClick = async () => {
        const start = historyStartWork.current.value
        const end = historyEndWork.current.value
        if (start === "" || end === "") {
            setError('Введите дату')
        } else {
            if (start !== '' || end !== '') {
                setError('Выберите дату')
            }
            if (end < start) {
                setError('Конечная дата меньше начальной')
            } else {

                const filter = {
                    startdate: historyStartWork.current.value,
                    enddate: historyEndWork.current.value,
                }
                setError('')

                const fill = async () => {
                    const res = await axios.post('/api/post/Reports',filter)
                    setPosts(res.data)
                    setError('')
                }
                fill()
            }
        }
    }
    const handleClickCancel = async () =>{
        setError('')
        setIsFilter(false)
        historyStartWork.current.value = ''
        historyEndWork.current.value = ''
    }
    const rowsSort = () =>{
        const c=document.getElementById("report");

        if(historyStartWork.current.value !=='') {
            const sort = currentlyPosts.sort((a, b) => b.row_number - a.row_number)
            console.log('SORT=', sort)
        }
    }
    const FIOSort = () =>{
        if(historyStartWork.current.value !=='') {
            const sort = currentlyPosts.sort((a, b) => a.user_name > b.user_name ? 1 : -1);
            console.log('SORT=', sort)
        }
    }
    const workstartSort = () =>{
        if(historyStartWork.current.value !=='') {
            const sort = currentlyPosts.sort((a, b) => a.workstart > b.workstart ? 1 : -1);
            console.log('SORT=', sort)
        }
    }
    const data = [
        { name: 'John', age: 30, email: 'john@example.com' },
        { name: 'Jane', age: 25, email: 'jane@example.com' },
        { name: 'Doe', age: 35, email: 'doe@example.com' }
    ];



    const requestSort = (key) => {
        let direction = 'ascending';
        if (sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    return (
        <>
            <Topbar/>
            <div className='Reports'>
                <div className="ReportsContainer">
                    <h2 className='ReportsMainText'>Отчет</h2>
                    <hr/>
                    {error && <div className='errorHistoryInfo'>{error}</div>}
                    <br/>
                    <input
                        type='date'
                        placeholder="Начало работы"
                        className="ModalInput"
                        ref={historyStartWork}
                        style={{'marginRight': '10px'}}
                    />
                    <input
                        type='date'
                        placeholder="Конец работы"
                        className="ModalInput"
                        ref={historyEndWork}
                        style={{'marginRight': '10px'}}
                    />
                    <button className="filterButton" onClick={handleClick}>ок</button>
                    <button className="filterButton" onClick={handleClickCancel}>сбросить</button>
                    <br/>
                    <br/>
                    <hr/>
                    <br/>
                        <tr>
                            <td className="historyinfoNumber" onClick={() => requestSort('name')}>
                                № {sortConfig.key === 'name' && (
                                <span>{sortConfig.direction === 'ascending' ? '▲' : '▼'}</span>
                            )}

                            </td>
                            <td className="historyinfoNumber">ФИО
                                <div className='report' onClick={FIOSort}>▲</div>
                            </td>
                            <td className="historyinfoNumber">Начало
                                <div className='report' onClick={workstartSort}>▲</div>
                            </td>
                            <td className="historyinfoNumber">Конец
                                <div className='report' onClick={()=>console.log('')}>▲</div>
                            </td>
                            <td className="historyinfoNumber">Длительность
                                <div className='report' onClick={()=>console.log('')}>▲</div>
                            </td>
                            <td className="historyinfoNumber">Коментарий
                                <div className='report' onClick={()=>console.log('')}>▲</div>
                            </td>
                        </tr>
                    {
                    currentlyPosts.map((posts, id) =>  <RepostsInfo key={id} posts={posts} username={username}/>)
                    }

                    <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate} currentPage={currentPage}/>

                </div>
            </div>
        </>
    )
}
