import React from 'react';
import axios from "axios";

function Role({ roles }) {
    // Проверяем, были ли переданы роли
    if (!roles) {
        return <div>Loading...</div>;
    }

    return (
        <div className="ReportsButton">
            <div style={{ display: 'flex', gap: '200px' }}>
                <div>
                    <label>Имя сотрудника</label>
                    <input className="ModalInputUpdate" type="text" />
                </div>
                <div>
                    <label>Роль</label>
                    <select className="ModalInputUpdate">
                        {roles.map((role, index) => (
                            <option
                                key={index}
                                value={role.id}
                            >
                                {role.name}
                            </option>
                        ))}
                    </select>
                </div>
            </div>
        </div>
    );
}

export async function getServerSideProps() {
    try {
        const res = await axios.get('http://localhost:3000/api/roles/'); // Замените на ваш абсолютный URL
        const roles = res.data[0].get_roles;
        console.log('roles=', roles);
        return {
            props: { roles }, // Передаем данные в компонент через props
        };
    } catch (error) {
        console.error("Error fetching roles:", error);
        return {
            props: { roles: [] }, // Возвращаем пустой массив в случае ошибки
        };
    }
}

export default Role;
