import React from 'react'
import { Link, useParams } from 'react-router-dom'
import { CircularProgress } from '@material-ui/core'
import axios from 'axios'
import { useEffect, useState } from 'react'

import SidebarDeps from '../SidebarDeps/SidebarDeps'
import './Sidebar.css'
import TreeView from 'react-treeview';
import 'react-treeview/react-treeview.css'

export default function Sidebar() {
  const [deps, setDeps] = useState([])
  const [users, setUsers] = useState([])
  const [isFetching, setIsFetching] = useState(false)
  const [filterDeps, setFilterDeps] = useState([])
  const currentUrl = useParams().username
  const localUser = JSON.parse(localStorage.getItem('user'))

  const dataSource = [

    {
      type: 'Служба поддержски продуктовых систем розничного бизнеса',id:1,
      collapsed: false,
      child: [
        {name: 'Отдел администрирования бэк-офисных систем розничного бизнеса',collapsed: false,id:2},
        {name: 'Отдел круглосуточной поддержски процессов закрытия операционного дня отчетности розничного бизнеса', collapsed: false,id:3},
        {name: 'Отдел сопровождения активных операций ФЛ', collapsed: false,id:4},
        {name: 'Отдел сопровождения пассивных операций ФЛ', collapsed: false,id:5}
      ],
    },
    {
      type: 'Служба поддержски централизованных и технологических процессов',id:6,
      collapsed: false,
      child: [
        {name: 'Отдел поддержски обмена с ФОИВ', collapsed: false,id:7},
        {name: 'Отдел поддержски систем электронного хранения', collapsed: false,id:8},
        {name: 'Отдел поддержски технологиеских процессов',id:9,
          child: [{name:'Группа поддержки зарплатных проектов',id:10,collapsed: false,
            child: [{name:'Группа поддержки логичесих карт и технологических сервисов',id:11}]}], collapsed: false},
        {name: 'Отдел сопровождения централизированных приложений',collapsed: false,id:12}
      ],
    },

    {
      type: 'Служба сопровождения муниципальных и специальных сервисов',id:13,
      collapsed: false,
      child: [
        {name: 'Отдел сопровождения муниципальных проектов', collapsed: false,id:14},
        {name: 'Отдел сопровождения специальных сервисов', collapsed: false,id:15}

      ],
    },
    {
      type: 'Служба сопровождения операционной и расчетной деятельности',id:16,
      collapsed: false,
      child: [
        {name: 'Отдел поддержки пользователей прочих АБС', collapsed: false,id:17},
        {name: 'Отдел систем доставки электронных документов',id:18,
          child: [
            {name:'Группа поддежрски SWIFT/КФТ/TELEX',id:19}], collapsed: false},
        {name: 'Отдел сопровождения АБС ЮЛ, систем расчетов и задач Казначейства',id:20,
          child: [{name:'Группа интеграции с внешними системами',id:21,collapsed: false,
            child: [{name:'Группа поддержки пользователей восточной зоны РФ',id:22,collapsed: false,
              child: [{name:'Группа поддержки пользователей европейской зоны РФ',id:23,collapsed: false,
            child: [{name:'Группа сопровождения систем расчетов',id:24}]}]}]}], collapsed: false},
        {name: 'Отдел сопровождения операционного дня', collapsed: false,id:25},
        {name: 'Отдел специального технологического сопровождения АБС', collapsed: false,id:26},
        {name: 'Отдел управления изменениями', collapsed: false,id:27}

      ],
    }


/*    {
      type: 'CEO',
      collapsed: false,
      child: [
        {name: 'Drew Anderson', age: 39, sex: 'male', role: 'boss', collapsed: false},
      ],
    },*/
  ];
  // загрузка всех департаментов
  useEffect(() => {
    // сортировка на дочерние и тд
    const getDeps = async () => {
        setIsFetching(true)
        const res = await axios.get('/api/departments/')
      let id = res.data.map(newarray => newarray.department_name)
      console.log("departaments=",res.data)

        for(let i = 0; i < res.data.length-1; i++){
          for(let j = i+1; j < res.data.length; j++){
            if(res.data.find(k => k === res.data[j].id && k !== res.data[i].id)){
              if(res.data[j].margin ){
                res.data[j].margin += 40
              } else {
                res.data[j].margin = 40
              }
            }
          }
        }

      let newArr = res.data.filter(d => d.id !== 7)
      newArr.push(res.data[6])
      global.xz=res.data[0].department_name
      console.log("NEWARRAY=",global.xz)
      setFilterDeps(newArr.filter(a => a.dep_id === 1))
      setDeps(newArr)
      if(currentUrl && currentUrl !== localUser._id){
        const arr1 = [1, 2, 3, 4, 5, 6, 7, '61efa2f6cf0ce39b27ba2a53', '61efa304cf0ce39b27ba2a54', '61efa31dcf0ce39b27ba2a55', '61efa32bcf0ce39b27ba2a56', '61efa33acf0ce39b27ba2a57', '61efa344cf0ce39b27ba2a58', '61efa34fcf0ce39b27ba2a59']
        const arr2 = [6, 8, 9, 10, 11, '61efa344cf0ce39b27ba2a58', '61efa35dcf0ce39b27ba2a5a', '61efa361cf0ce39b27ba2a5b', '61efa36acf0ce39b27ba2a5c', '61efa374cf0ce39b27ba2a5d']

        if(arr1.find(i => i === currentUrl)){
          setFilterDeps(newArr.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
        }
        if(arr2.find(i => i === currentUrl)){
          setFilterDeps(newArr)
        }else{
          setFilterDeps(newArr.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
        }
      }
      setIsFetching(false)
    }

    // загрузка пользователей и сортировка по департаментам
    const fetchUser = async () => {
      setIsFetching(true)
      const res = await axios.get('/api/user')
      const id=res.data.sort((a, b) => a.user_name.localeCompare(b.user_name))
      setUsers(res.data)
      setIsFetching(false)
      console.log("ID=",id)
    }

    fetchUser()
    getDeps()
  }, [])

  const isShowDeps = (e) => {
    const arr1 = [1, 2, 3, 4, 5, 6, 7, '61efa2f6cf0ce39b27ba2a53', '61efa304cf0ce39b27ba2a54', '61efa31dcf0ce39b27ba2a55', '61efa32bcf0ce39b27ba2a56', '61efa33acf0ce39b27ba2a57', '61efa344cf0ce39b27ba2a58', '61efa34fcf0ce39b27ba2a59']
    const arr2 = [6, 8, 9, 10, 11, '61efa344cf0ce39b27ba2a58', '61efa35dcf0ce39b27ba2a5a', '61efa361cf0ce39b27ba2a5b', '61efa36acf0ce39b27ba2a5c', '61efa374cf0ce39b27ba2a5d']

    if(e.target.value === 1){
      setFilterDeps(deps.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
    }
    if(arr2.find(i => i === e.target.value)){
      setFilterDeps(deps)
    }else{
      setFilterDeps(deps.filter(dep => dep.dep_id === 1 || dep.dep_id === 2 || dep.dep_id === 3 || dep.dep_id === 4 || dep.dep_id === 5 || dep.dep_id === 6 || dep.dep_id === 7))
    }
  }
  const click= async ()=>{
    const res = await axios.get('/api/departments/')
    let id = res.data.map(newarray => newarray.parent_department)
      console.log(id)

  }

  return (
    <div className='sidebar'>
      <div className="sidebarWrapper">
        <div className="tree">
          <div className="treeWrapper">
{/*
            {isFetching ? <CircularProgress style={{color:'#1877f2', margin:'30%'}}/> : filterDeps.map((dep, id) => <SidebarDeps dep={dep} key={id} users={users} setIsShowDeps={isShowDeps}/>)}
            {deps.map((dep, id) => {

              const type = dep.type
              const label = <span className="node">{dep.department_name}</span>
              return (
                  <TreeView key={type + '|' + id} nodeLabel={label} defaultCollapsed={true}>

                  <TreeView defaultCollapsed={true}>

                  <div className="info">{dep.parent_department}</div>
                </TreeView>
              </TreeView>
              )})}*/}

            {dataSource.map((node, i) => {

              const label = <Link to={`/department/${node.id}`} className='depsUser' style={{fontWeight: `bold`}}><span className="node">{node.type}</span></Link>

              return (

                  <TreeView key={node.type + '|' + i} nodeLabel={label} defaultCollapsed={true}>

                    {node.child.map(departments => {

                      const label2 = <Link to={`/department/${departments.id}`} className='depsUser'><span className="node">{departments.name}</span></Link>

                      return (

                            <TreeView nodeLabel={label2} key={departments.name} defaultCollapsed={true}>

                              {!!departments.child ? departments.child.map(departments1  => {

                            console.log("CHILD=",departments.child)

                                return(

                                <Link to={`/department/${departments1.id}`} className='depsUser'>

                                  <div className="info" style={{paddingBottom:'8px'}}>{departments1.name}</div>

                                </Link>)
                              }) :''}

                            </TreeView>

                              );
                    })}
                  </TreeView>
