import React from 'react';
import Modal from "../../../Templates/Modal/Modal";
import axios from "axios";


function Role({modalActive,setModalActive,username,user}) {
    const handleRole = async (e) => {
        e.preventDefault()

        const newPost = {
            userId: username.username,
            healthEnd: 1,
            status: 4
        }
        try {
            await axios.put('/api/deluser/', newPost)

            setModalActive(false)
            window.location.href = `/profile/${user.id}`
        } catch (error) {
            console.log(error);
        }
    }
    return (
        <div>
            <Modal active={modalActive} setActive={setModalActive}>
                <h1>Назначить роль:</h1>
                <hr/>
                <form className="modalLoginBox" onSubmit={handleRole}>
                    <h2>Назначить роль</h2>
                    <button className="ModalButtonDelete" type="submit">
                        Назначить роль
                    </button>
                </form>
            </Modal>
        </div>
    );

}

export default Role;
