import Staff from '../Staff/Staff'
import './Feed.css'
import React, {useEffect, useRef, useState} from 'react'


export default function Feed() {
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        // Симуляция процесса загрузки
        const timer = setInterval(() => {
            if (progress < 100) {
                setProgress(progress + 1);
            } else {
                clearInterval(timer);
            }
        }, 50);

        return () => clearInterval(timer);
    }, [progress]);

  return (
    <div className='feed'>
      <div>
          <h4 className='h1'>Сервис находится в тестовом режиме  ➜ <a className="a4" href="https://sfera.vtb.ru/knowledge/pages?id=110481" target="_blank">Нашли ошибку? Пиши в комментариях!</a></h4>
      </div>
      <div className="feedWrapper">

        <Staff user={"user"} dep={"depppp"} func={(xz)=>{console.log(xz)}}/>
      </div>

        <div className="circle-container">
            <div className="circle">
                <img className="circle-image" src='../images/enot.gif' alt="Enot Gif" />
                <div className="progress" style={{transform: `rotate(${(progress / 100) * 360}deg)`}}></div>
            </div>
        </div>


    </div>
  )
}
