        child()
        get_statistics_all_users()
        see()
    }, [departId])

///////////////////////////////////////////////////////////////////////////////////////////////////

    // фильтр пользователей по статусу
    function filterUsersFoo(users) {
        return users.filter(user => user.status === 1)
            .concat(users.filter(user => user.status === 2))
            .concat(users.filter(user => user.status === 3))
            .concat(users.filter(user => user.status !== 1 && user.status !== 2 && user.status !== 3 && user.status !== 4))
            .concat(users.filter(user => user.status === 4))
    }
///////////////////////////////////////////////////////////////////////////////////////////////////


///////////////////////////////////////////////////////////////////////////////////////////////////

    // добавление дочерних по checkbox
    const clickcheckbox = async () => {
        setChecked(!checked)

        const info = {
            userId: localUser._id,
            ischeked: !checked
        }
        await axios.post('/api/child/', info)

    }
///////////////////////////////////////////////////////////////////////////////////////////////////
    // обновляем main department по кнопке изменить
    const handleModalClick = async () => {
        const add = {
            id: localUser._id,
            depsId: depsId
        }
        const res = await axios.put('/api/user', add)
        setModalMain(false)

    }
///////////////////////////////////////////////////////////////////////////////////////////////////
    // функция get_users_by_dep
    const get_users_by_dep = async () => {
    if(checked===true){
        setUs([])
    }else{

    }
        if (isJob) {
            const info = {
                depsId: depsId
            }
            const res = await axios.post('/api/get_users_by_dep/', info)
            const dep_users = res.data[0]['get_users_by_dep']

            if (dep_users === null) {

            } else {
                dep_users.sort((a, b) => a.user_name.localeCompare(b.user_name))
                let filterUsers = filterUsersFoo(dep_users)
                console.log('filterUsers=',filterUsers)
                setUsersDeps(filterUsers)
                setIsOpen(true)
                setIsJob(!isJob)
            }
        }
    }
///////////////////////////////////////////////////////////////////////////////////////////////////
    return (
        <div className='staff'>
            {departId && <>
                {isJob ? <button onClick={get_users_by_dep} className="btnStaff" style={{width: '155px'}}>Показать
                        дочерние</button> :
                    <button onClick={() => window.location.href = `/department/${departId}`} className="btnStaff">Скрыть
                        дочерние</button>}
                <button onClick={() => setModalMain(true)} className='btnStaff' style={{float: 'right'}}>Сделать
                    главной
                </button>
            </>}

            <div className="employeeWrapper">
                {isFetching
                    ? <div className='isFetching'>Загрузка...</div>
                    : users.length === 0 && getUser.length !== 0
                        ? ''
                        : !!departId  && !checked
                            ? !isOpen
                                ? users.filter(user => user.department_id === departId).map((user, id) => <Employee
                                    key={id} user={user}/>)
                                : ''
                            : statisticsWork.map((user, id) => <Employee key={id} user={user} />)
                }

                {
                    checked
                        ? us.map((user, id) => <Employee key={id} user={user}/>)
                        : ''
                }


                {
                    !!isOpen
                        ?
                        UsersDep.map((user, id) => <Employee key={id} user={user}/>)
                        : ""
                }

                {
                    !!getUser
                        ?
                        getUser.map((user, id) => <Employee key={id} user={user}/>)
                        : ""
                }

            </div>
            <Modal active={modalMain} setActive={setModalMain}>
                <h1>Изменение информации:</h1>
                <hr/>
                <form className="modalLoginBox">
                    <label>
                        {
                            <input
                            type='checkbox'
                            onChange={clickcheckbox}
                            checked={checked}
                        />
                        }Добавить дочерние</label>
                    <button className="ModalButton" type="submit" disabled={isFetching} onClick={handleModalClick}>
                        Изменить
                    </button>
                </form>
            </Modal>
        </div>
    )
}
